import React, { useState, Suspense } from 'react';
import PatientSelector from './PatientSelector';
import LoadingSpinner from './components/ui/LoadingSpinner';
import ErrorBoundary from './components/ErrorBoundary';
import { PATIENTS } from './constants/index';
import './App.css';

// Lazy load components for better performance
const PatientDashboard = React.lazy(() => import('./components/Dashboard'));
const PhysicianDashboard = React.lazy(() => import('./components/Dashboard'));

/**
 * Main Health Dashboard Application
 * Features both Patient and Physician views with role toggle
 */
function App() {
  const [selectedPatient, setSelectedPatient] = useState('Patient_001');
  const [currentRole, setCurrentRole] = useState('patient'); // 'patient' or 'physician'

  const toggleRole = () => {
    setCurrentRole(prev => prev === 'patient' ? 'physician' : 'patient');
  };

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <h1>üè• Health Dashboard</h1>

          {/* Role Toggle */}
          <div className="role-toggle">
            <label className="toggle-label">
              <input
                type="checkbox"
                checked={currentRole === 'physician'}
                onChange={toggleRole}
                aria-label="Toggle between patient and physician view"
              />
              <span className="toggle-slider"></span>
              <span className="role-text">
                {currentRole === 'patient' ? 'üë§ Patient View' : 'üë®‚Äç‚öïÔ∏è Physician View'}
              </span>
            </label>
          </div>

          {/* Patient Selector */}
          <div className="patient-selector">
            <label htmlFor="patient-select">Select Patient:</label>
            <PatientSelector
              id="patient-select"
              patients={PATIENTS}
              value={selectedPatient}
              onChange={setSelectedPatient}
            />
          </div>
        </div>
      </header>

      <main className="app-main">
        <ErrorBoundary>
          <Suspense fallback={<LoadingSpinner message="Loading dashboard..." />}>
            <PatientDashboard 
              patientId={selectedPatient} 
              viewMode={currentRole}
            />
          </Suspense>
        </ErrorBoundary>
      </main>
    </div>
  );
}

export default App;
