import React, { useState, useMemo } from 'react';
import PropTypes from 'prop-types';

// Simple chart placeholder until recharts is properly integrated
const SimpleLineChart = ({ data, height = 300 }) => (
  <div style={{ 
    height, 
    border: '1px solid #ddd', 
    borderRadius: '4px', 
    background: '#f9f9f9',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px'
  }}>
    <h4>Glucose Chart</h4>
    <p>{data.length} data points available</p>
    <p style={{ fontSize: '12px', color: '#666', marginTop: '10px' }}>
      Chart visualization coming soon...
    </p>
  </div>
);

/**
 * Unified Glucose Chart component for both Patient and Physician views
 */
function GlucoseChart({ patientId, data, viewMode = 'patient', isExpanded = false }) {
  const [currentWeek, setCurrentWeek] = useState(new Date(2025, 4, 1)); // May 1, 2025

  // Extract glucose data
  const glucoseData = data?.glucoseData || [];

  // Memoized chart data processing
  const { chartData, weekRange } = useMemo(() => {
    if (!glucoseData.length) return { chartData: [], weekRange: null };

    // Calculate week range
    const startOfWeek = new Date(currentWeek);
    startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay());
    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(endOfWeek.getDate() + 6);

    // Filter data for current week
    const weekData = glucoseData.filter(d => {
      const date = new Date(d.timestamp || d.date);
      return date >= startOfWeek && date <= endOfWeek;
    });

    // Format data for Recharts
    const formattedData = weekData.map(d => {
      const date = new Date(d.timestamp || d.date);
      return {
        time: date.toLocaleDateString('en-US', { weekday: 'short', day: 'numeric' }) + 
              ' ' + date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' }),
        value: d.value || d.glucoseLevel,
        type: d.measurementType || d.type,
        range: d.range,
        fullDate: date
      };
    });

    return {
      chartData: formattedData,
      weekRange: {
        start: startOfWeek,
        end: endOfWeek
      }
    };
  }, [glucoseData, currentWeek]);

  // Navigation handlers
  const goToPrevWeek = () => {
    setCurrentWeek(prev => {
      const newWeek = new Date(prev);
      newWeek.setDate(newWeek.getDate() - 7);
      return newWeek;
    });
  };

  const goToNextWeek = () => {
    setCurrentWeek(prev => {
      const newWeek = new Date(prev);
      newWeek.setDate(newWeek.getDate() + 7);
      return newWeek;
    });
  };

  // Get color based on glucose range
  const getGlucoseColor = (range) => {
    switch (range?.toLowerCase()) {
      case 'below range': return '#FFC107';
      case 'in range': return '#4CAF50';
      case 'above range': return '#F44336';
      default: return '#2196F3';
    }
  };

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="glucose-tooltip">
          <p className="tooltip-label">{label}</p>
          <p className="tooltip-value">
            <span style={{ color: getGlucoseColor(data.range) }}>
              Glucose: {data.value} mg/dL
            </span>
          </p>
          <p className="tooltip-info">
            Range: {data.range || 'Unknown'}
          </p>
          <p className="tooltip-info">
            Type: {data.type || 'Unknown'}
          </p>
        </div>
      );
    }
    return null;
  };

  // Show loading/error states
  if (!glucoseData.length) {
    return (
      <div className="chart-placeholder">
        <p>No glucose data available for {patientId}</p>
      </div>
    );
  }

  if (!chartData.length) {
    return (
      <div className="chart-placeholder">
        <ChartNavigation
          onPrevious={goToPrevWeek}
          onNext={goToNextWeek}
          currentPeriod={weekRange ? 
            `${weekRange.start.toLocaleDateString()} - ${weekRange.end.toLocaleDateString()}` : 
            'No data'
          }
          periodType="week"
        />
        <p>No glucose data available for this week</p>
      </div>
    );
  }

  return (
    <div className="glucose-chart">
      <ChartNavigation
        onPrevious={goToPrevWeek}
        onNext={goToNextWeek}
        currentPeriod={weekRange ? 
          `${weekRange.start.toLocaleDateString()} - ${weekRange.end.toLocaleDateString()}` : 
          'Loading...'
        }
        periodType="week"
      />
      
      <div className="chart-content" style={{ height: isExpanded ? 450 : 300 }}>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
            <CartesianGrid strokeDasharray="3 3" opacity={0.3} />
            <XAxis 
              dataKey="time" 
              fontSize={12}
              angle={-45}
              textAnchor="end"
              height={60}
            />
            <YAxis 
              domain={[60, 200]}
              fontSize={12}
              label={{ value: 'Glucose (mg/dL)', angle: -90, position: 'insideLeft' }}
            />
            <Tooltip content={<CustomTooltip />} />
            
            {/* Reference lines for glucose ranges */}
            <ReferenceLine y={80} stroke="#FFC107" strokeDasharray="5 5" />
            <ReferenceLine y={140} stroke="#FFC107" strokeDasharray="5 5" />
            
            <Line 
              type="monotone" 
              dataKey="value" 
              stroke="#2196F3"
              strokeWidth={2}
              dot={{ fill: '#2196F3', strokeWidth: 2, r: 4 }}
              activeDot={{ r: 6 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
      
      {/* Legend */}
      <div className="chart-legend">
        <div className="legend-item">
          <div className="legend-color" style={{ backgroundColor: '#4CAF50' }}></div>
          <span>In Range (80-140 mg/dL)</span>
        </div>
        <div className="legend-item">
          <div className="legend-color" style={{ backgroundColor: '#FFC107' }}></div>
          <span>Below/Above Range</span>
        </div>
      </div>

      {/* Educational info for patient view */}
      {viewMode === 'patient' && (
        <div className="educational-info">
          <h4>Understanding Your Glucose Levels</h4>
          <p>Normal glucose levels are typically between 80-140 mg/dL. Monitor your levels regularly and consult your healthcare provider about any concerning patterns.</p>
        </div>
      )}
    </div>
  );
}

GlucoseChart.propTypes = {
  patientId: PropTypes.string.isRequired,
  data: PropTypes.object.isRequired,
  viewMode: PropTypes.oneOf(['patient', 'physician']),
  isExpanded: PropTypes.bool
};

export default GlucoseChart;
