import React from 'react';

const BodySvg = ({ painLocation, painColor, className }) => {
  const defaultFill = '#D3D3D3';

  const bodyPartFills = {
    head: defaultFill,
    neck: defaultFill,
    torso: defaultFill,
    arms: defaultFill,
    legs: defaultFill,
    feet: defaultFill,
  };

  const locationMapping = {
    head: 'head',
    neck: 'neck',
    torso: 'torso',
    chest: 'torso',
    stomach: 'torso',
    back: 'torso',
    arms: 'arms',
    shoulders: 'arms',
    hands: 'arms',
    legs: 'legs',
    knees: 'legs',
    thighs: 'legs',
    feet: 'feet',
    ankles: 'feet',
  };

  const targetPart = locationMapping[painLocation];
  if (targetPart) {
    bodyPartFills[targetPart] = painColor;
  }

  return (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 300 400" className={className}>
      <g id="body-outline">
        <ellipse id="head" cx="150" cy="40" rx="25" ry="30" fill={bodyPartFills.head} />
        <rect id="neck" x="135" y="70" width="30" height="20" fill={bodyPartFills.neck} />
        <path id="torso" d="M120,90 h60 v100 h-60 z" fill={bodyPartFills.torso} />
        <g id="arms">
          <rect x="80" y="100" width="25" height="80" fill={bodyPartFills.arms} />
          <rect x="195" y="100" width="25" height="80" fill={bodyPartFills.arms} />
        </g>
        <g id="legs">
          <rect x="130" y="200" width="20" height="120" fill={bodyPartFills.legs} />
          <rect x="150" y="200" width="20" height="120" fill={bodyPartFills.legs} />
        </g>
        <g id="feet">
          <ellipse cx="140" cy="330" rx="15" ry="8" fill={bodyPartFills.feet} />
          <ellipse cx="160" cy="330" rx="15" ry="8" fill={bodyPartFills.feet} />
        </g>
      </g>
    </svg>
  );
};

export default BodySvg;
