import React from 'react';
import VisualizationWrapper from './VisualizationWrapper';
import PhysicianVisualizationWrapper from './PhysicianVisualizationWrapper';
import './DashboardGrid.css';

/**
 * Shared component for rendering dashboard grids with visualization wrappers
 * Handles the repetitive VisualizationWrapper rendering logic
 */
const DashboardGrid = ({
  viewMode, // 'patient' or 'physician'
  selectedVisualizations,
  availableVisualizations,
  allVisualizations,
  onVisualizationChange,
  onExpand,
  expandedItem,
  renderVisualization,
  disabled = false,
  placeholderText = "No data available for this patient.",
  // New props for physician summary functionality
  showSummaries = false,
  summaryTexts = {},
  onSummaryChange = null
}) => {
  // Configuration for different view modes
  const gridConfig = {
    patient: {
      className: 'dashboard-grid',
      chartIds: ['box1', 'box2', 'box3'],
      chartClassNames: ['visualization-box', 'visualization-box-1', 'visualization-box-2', 'visualization-box-3']
    },
    physician: {
      className: 'physician-charts-grid',
      chartIds: ['window1', 'window2', 'window3', 'window4', 'window5', 'window6'],
      chartClassNames: ['physician-chart-container', 'window-1', 'window-2', 'window-3', 'window-4', 'window-5', 'window-6']
    }
  };

  const config = gridConfig[viewMode];

  // Choose the appropriate wrapper component
  const WrapperComponent = (viewMode === 'physician' && showSummaries) 
    ? PhysicianVisualizationWrapper 
    : VisualizationWrapper;

  // If there's an expanded item, show only that
  if (expandedItem) {
    const wrapperProps = {
      id: expandedItem,
      className: `${config.chartClassNames[0]} expanded-view`,
      selectedViz: selectedVisualizations[expandedItem],
      availableVisualizations: availableVisualizations,
      allVisualizations: allVisualizations,
      onVisualizationChange: onVisualizationChange,
      onExpand: onExpand,
      isExpanded: true,
      renderVisualization: renderVisualization,
      disabled: disabled,
      placeholderText: placeholderText
    };

    // Add physician-specific props if using PhysicianVisualizationWrapper
    if (viewMode === 'physician' && showSummaries) {
      wrapperProps.showSummary = true;
      wrapperProps.summaryText = summaryTexts[expandedItem] || '';
      wrapperProps.onSummaryChange = onSummaryChange;
    }

    return <WrapperComponent {...wrapperProps} />;
  }

  // Render the grid of visualization wrappers
  return (
    <div className={config.className}>
      {config.chartIds.map((chartId, index) => {
        const wrapperProps = {
          key: chartId,
          id: chartId,
          className: `${config.chartClassNames[0]} ${config.chartClassNames[index + 1] || ''}`,
          selectedViz: selectedVisualizations[chartId],
          availableVisualizations: availableVisualizations,
          allVisualizations: allVisualizations,
          onVisualizationChange: onVisualizationChange,
          onExpand: onExpand,
          isExpanded: expandedItem === chartId,
          renderVisualization: renderVisualization,
          disabled: disabled,
          placeholderText: placeholderText
        };

        // Add physician-specific props if using PhysicianVisualizationWrapper
        if (viewMode === 'physician' && showSummaries) {
          wrapperProps.showSummary = true;
          wrapperProps.summaryText = summaryTexts[chartId] || '';
          wrapperProps.onSummaryChange = onSummaryChange;
        }

        return <WrapperComponent {...wrapperProps} />;
      })}
    </div>
  );
};

export default DashboardGrid;
