/**
 * PatientBasicInfo.js - Patient Basic Information Component
 * 
 * Displays basic patient demographics and identification information.
 * Part of the split patient information layout.
 */

import React from 'react';
import './PatientInfoBox.css';

const PatientBasicInfo = ({ 
  patientInfo, 
  loading = false, 
  error = null, 
  className = '' 
}) => {
  if (loading) {
    return (
      <div className={`patient-info-box basic-info ${className}`}>
        <h3>Patient Information</h3>
        <p>Loading patient info...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`patient-info-box basic-info ${className}`}>
        <h3>Patient Information</h3>
        <p>Error loading data.</p>
      </div>
    );
  }

  if (!patientInfo) {
    return (
      <div className={`patient-info-box basic-info ${className}`}>
        <h3>Patient Information</h3>
        <p>No patient data found.</p>
      </div>
    );
  }

  return (
    <div className={`patient-info-box basic-info ${className}`}>
      <h3>Patient Information</h3>
      <div className="info-content">
        <div className="info-item">
          <span className="info-label">Name:</span>
          <span className="info-value">{patientInfo.name}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Patient #:</span>
          <span className="info-value">{patientInfo.patientNumber}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Age:</span>
          <span className="info-value">{patientInfo.age} years</span>
        </div>
        <div className="info-item">
          <span className="info-label">Gender:</span>
          <span className="info-value">{patientInfo.gender || 'Not specified'}</span>
        </div>
      </div>
    </div>
  );
};

export default PatientBasicInfo;
