/**
 * Factory for creating chart configurations
 * Eliminates duplicate getChartConfig functions across chart components
 * Now provides uniform dimensions for consistent aspect ratios
 */
export const createChartConfig = (type, isExpanded, containerWidth = 400) => {
  const baseConfig = {
    expanded: isExpanded,
    containerWidth
  };

  // Universal dimensions for consistent aspect ratios
  const universalConfig = {
    // Dashboard view dimensions
    dashboard: {
      width: 400,
      height: 300,
      padding: { top: 50, right: 30, bottom: 60, left: 50 },
      dayWidth: 45,
      fontSize: {
        yAxis: 12,
        yAxisTitle: 14,
        timeLabel: 10,
        dayLabel: 10,
        dateLabel: 10,
      },
      barWidth: 6,
    },
    // Expanded view dimensions
    expanded: {
      width: 600,
      height: 450,
      padding: { top: 60, right: 40, bottom: 80, left: 60 },
      dayWidth: 70,
      fontSize: {
        yAxis: 14,
        yAxisTitle: 16,
        timeLabel: 12,
        dayLabel: 12,
        dateLabel: 12,
      },
      barWidth: 8,
    }
  };

  const config = isExpanded ? universalConfig.expanded : universalConfig.dashboard;

  switch (type) {
    case 'glucose':
      return {
        ...config,
        yAxisLabels: [0, 4, 8, 12],
        yAxisRange: 12,
      };

    case 'bloodPressure':
      return {
        ...config,
        yAxisRange: { systolic: 200, diastolic: 120 },
        yAxisOffset: { systolic: 0, diastolic: 0 },
        yAxisLabels: {
          systolic: isExpanded ? [0, 40, 80, 120, 160, 200] : [0, 100, 200],
          diastolic: isExpanded ? [0, 20, 40, 60, 80, 100, 120] : [0, 60, 120],
        },
        dayPadding: isExpanded ? 8 : 4,
      };

    case 'exercise':
      return {
        ...config,
        fontSize: {
          ...config.fontSize,
          emoji: isExpanded ? 10 : 8,
        },
      };

    case 'mealContents':
      // Special handling for meal contents due to complex SVG structure
      const mealMaxWidth = Math.min(containerWidth - 40, isExpanded ? 700 : 500);
      const mealAvailableWidth = mealMaxWidth - 160;

      let mealDayWidth, mealTotalWidth, mealPadding;

      if (mealAvailableWidth < 420) {
        const adjustedPadding = Math.max(120, mealAvailableWidth * 0.25);
        const adjustedAvailableWidth = mealAvailableWidth - (160 - adjustedPadding);
        mealDayWidth = Math.max(50, adjustedAvailableWidth / 7);
        mealTotalWidth = adjustedPadding + (mealDayWidth * 7);
        mealPadding = { top: 40, right: 20, bottom: 80, left: adjustedPadding };
      } else {
        mealDayWidth = Math.max(60, Math.min(85, mealAvailableWidth / 7));
        mealTotalWidth = 160 + (mealDayWidth * 7);
        mealPadding = { top: 40, right: 30, bottom: 80, left: 130 };
      }

      const mealHeight = isExpanded ? 80 : 60;
      const totalMealHeight = 4 * mealHeight;
      const totalPadding = mealPadding.top + mealPadding.bottom;
      const mealMaxHeight = totalMealHeight + totalPadding;

      return {
        width: mealTotalWidth,
        height: mealMaxHeight,
        padding: mealPadding,
        dayWidth: mealDayWidth,
        mealHeight: mealHeight,
        fontSize: {
          dayLabel: isExpanded ? 10 : 8,
          dateLabel: isExpanded ? 8 : 6,
          mealLabel: isExpanded ? 8 : 6,
          timeLabel: isExpanded ? 6 : 5,
        },
      };

    case 'pain':
      // Special handling for pain visualization due to multiple containers
      return {
        ...config,
        // Pain charts may need additional space for body mapping
        height: isExpanded ? 500 : 350,
      };

    case 'sleep':
      return {
        ...config,
        yAxisLabels: [0, 2, 4, 6, 8, 10, 12],
        yAxisRange: 12,
      };

    case 'mood':
      return {
        ...config,
        yAxisLabels: [0, 25, 50, 75, 100],
        yAxisRange: 100,
      };

    default:
      return {
        ...config,
        yAxisLabels: [0, 25, 50, 75, 100],
        yAxisRange: 100,
      };
  }
};
