/**
 * Chart Font Size Configuration Utility
 * 
 * Provides consistent font size configurations for all chart components
 * based on whether they are in dashboard view (not expanded) or expanded view.
 * 
 * This ensures all charts have readable text in the dashboard view while
 * maintaining appropriate sizing for expanded views.
 */

/**
 * Get font size configuration for chart components
 * @param {boolean} isExpanded - Whether the chart is in expanded view
 * @returns {Object} Font size configuration object
 */
export const getChartFontSizes = (isExpanded = false) => {
  if (isExpanded) {
    return {
      yAxis: 16,
      yAxisTitle: 18,
      timeLabel: 14,
      dayLabel: 14,
      dateLabel: 14,
      mealLabel: 12,
      xAxis: 12,
      emoji: 12,
    };
  } else {
    // Dashboard view - ensure readable font sizes
    return {
      yAxis: 14,
      yAxisTitle: 16,
      timeLabel: 12,
      dayLabel: 12,
      dateLabel: 12,
      mealLabel: 12,
      xAxis: 12,
      emoji: 10,
    };
  }
};

/**
 * Get font size configuration for line charts (pain, sleep)
 * @param {boolean} isExpanded - Whether the chart is in expanded view
 * @returns {Object} Font size configuration object
 */
export const getLineChartFontSizes = (isExpanded = false) => {
  if (isExpanded) {
    return {
      yAxis: 12,
      xAxis: 12,
    };
  } else {
    // Dashboard view - ensure readable font sizes
    return {
      yAxis: 12,
      xAxis: 12,
    };
  }
};

/**
 * Get font size configuration for blood pressure charts
 * (Uses larger fonts for better readability)
 * @param {boolean} isExpanded - Whether the chart is in expanded view
 * @returns {Object} Font size configuration object
 */
export const getBloodPressureFontSizes = (isExpanded = false) => {
  if (isExpanded) {
    return {
      yAxis: 16,
      yAxisTitle: 18,
      timeLabel: 22,
      dayLabel: 22,
      dateLabel: 22,
    };
  } else {
    // Dashboard view - ensure readable font sizes
    return {
      yAxis: 14,
      yAxisTitle: 16,
      timeLabel: 19,
      dayLabel: 19,
      dateLabel: 19,
    };
  }
};
