import { useState, useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import SimpleDataService from '../services/dataService_simple';

/**
 * Custom hook for loading and managing patient health data
 * 
 * This hook handles:
 * - Loading patient data from CSV files
 * - Error handling with retry functionality
 * - Loading states
 * - Data caching to avoid unnecessary re-fetches
 * 
 * @param {string} patientId - Patient identifier (e.g., "Patient_001")
 * @returns {Object} Hook state and functions
 */
function usePatientData(patientId) {
  // State management
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastFetchedId, setLastFetchedId] = useState(null);

  /**
   * Load data for a specific patient
   * Uses useCallback to prevent unnecessary re-renders
   */
  const loadPatientData = useCallback(async (id) => {
    // Don't reload if we already have data for this patient
    if (id === lastFetchedId && data && !error) {
      return;
    }

    // Validate patient ID
    if (!id || typeof id !== 'string') {
      setError('Invalid patient ID provided');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);
      
      console.log(`Loading data for ${id}...`);
      const patientData = await SimpleDataService.getPatientData(id);
      
      // Validate that we got valid data
      if (!patientData || !patientData.patientInfo) {
        throw new Error('Invalid data structure received');
      }

      setData(patientData);
      setLastFetchedId(id);
      console.log(`Successfully loaded data for ${id}`);
      
    } catch (err) {
      console.error(`Failed to load data for ${id}:`, err);
      setError(err.message || 'Failed to load patient data');
      setData(null);
    } finally {
      setLoading(false);
    }
  }, [lastFetchedId, data, error]);

  /**
   * Retry loading data (useful for error recovery)
   */
  const retry = useCallback(() => {
    if (patientId) {
      // Reset the lastFetchedId to force a reload
      setLastFetchedId(null);
      loadPatientData(patientId);
    }
  }, [patientId, loadPatientData]);

  /**
   * Clear data (useful when switching patients)
   */
  const clearData = useCallback(() => {
    setData(null);
    setError(null);
    setLastFetchedId(null);
    setLoading(false);
  }, []);

  // Load data when patientId changes
  useEffect(() => {
    if (patientId) {
      loadPatientData(patientId);
    } else {
      // Clear data if no patient ID provided
      clearData();
    }
  }, [patientId, loadPatientData, clearData]);

  // Return hook state and functions
  return {
    // Data
    data,
    patientInfo: data?.patientInfo || null,
    
    // Individual data types for convenience
    glucoseData: data?.glucoseData || [],
    bloodPressureData: data?.bloodPressureData || [],
    exerciseData: data?.exerciseData || [],
    moodData: data?.moodData || [],
    painData: data?.painData || [],
    sleepData: data?.sleepData || [],
    mealData: data?.mealData || [],
    
    // State
    loading,
    error,
    
    // Actions
    retry,
    clearData,
    
    // Helper flags
    hasData: !!data,
    isEmpty: !loading && !error && !data,
    isError: !!error,
    
    // Data availability flags
    hasGlucoseData: (data?.glucoseData || []).length > 0,
    hasBloodPressureData: (data?.bloodPressureData || []).length > 0,
    hasExerciseData: (data?.exerciseData || []).length > 0,
    hasMoodData: (data?.moodData || []).length > 0,
    hasPainData: (data?.painData || []).length > 0,
    hasSleepData: (data?.sleepData || []).length > 0,
    hasMealData: (data?.mealData || []).length > 0
  };
}

// PropTypes for better documentation (though hooks don't technically use PropTypes)
usePatientData.propTypes = {
  patientId: PropTypes.string
};

export default usePatientData;
