/**
 * ChartSummary - Common summary component for chart statistics
 * 
 * This component provides a consistent way to display summary statistics
 * across all chart components, eliminating duplicate summary patterns.
 */

import React from 'react';
import './ChartSummary.css';

const ChartSummary = ({ 
  title, 
  value, 
  unit = '', 
  subtitle = '', 
  color = 'var(--chart-color-info)',
  className = '',
  size = 'medium'
}) => {
  return (
    <div className={`chart-summary ${size} ${className}`}>
      <div className="summary-title">{title}</div>
      <div className="summary-value" style={{ color }}>
        {value}{unit}
      </div>
      {subtitle && <div className="summary-subtitle">{subtitle}</div>}
    </div>
  );
};

// Summary Grid component for multiple summaries
const ChartSummaryGrid = ({ summaries, columns = 'auto-fit', className = '' }) => {
  return (
    <div 
      className={`summary-container ${className}`}
      style={{ 
        gridTemplateColumns: `repeat(${columns}, minmax(150px, 1fr))` 
      }}
    >
      {summaries.map((summary, index) => (
        <ChartSummary
          key={index}
          title={summary.title}
          value={summary.value}
          unit={summary.unit}
          subtitle={summary.subtitle}
          color={summary.color}
          size={summary.size}
        />
      ))}
    </div>
  );
};

export { ChartSummary, ChartSummaryGrid };
export default ChartSummary;
