// Application constants
export const PATIENT_COUNT = 100;

// Generate patient IDs array
export const PATIENTS = Array.from({ length: PATIENT_COUNT }, (_, i) => 
  `Patient_${(i + 1).toString().padStart(3, '0')}`
);

// Chart color scheme
export const CHART_COLORS = {
  glucose: '#3498db',
  bloodPressure: '#e74c3c',
  exercise: '#f39c12',
  mood: '#9b59b6',
  pain: '#e67e22',
  mealContents: '#27ae60',
  sleep: '#34495e'
};

// Visualization configurations
export const VISUALIZATION_CONFIG = {
  glucose: { name: 'Blood Glucose', color: CHART_COLORS.glucose },
  bloodPressure: { name: 'Blood Pressure', color: CHART_COLORS.bloodPressure },
  exercise: { name: 'Exercise Chart', color: CHART_COLORS.exercise },
  mood: { name: 'Mood Calendar', color: CHART_COLORS.mood },
  pain: { name: 'Pain Report', color: CHART_COLORS.pain },
  mealContents: { name: 'Meal Contents', color: CHART_COLORS.mealContents },
  sleep: { name: 'Sleep Chart', color: CHART_COLORS.sleep }
};

// Default patient data when no real data is available
export const getDefaultPatientInfo = (patientId) => ({
  name: patientId,
  age: 'N/A',
  diseases: [],
  medications: { scheduled: [], asNeeded: [] }
});
