import React from 'react';
import './ChartContainer.css';

const ChartContainer = ({ 
  title, 
  children, 
  isLoading = false, 
  error = null, 
  noData = false, 
  isExpanded = false, 
  onExpand, 
  onPrev, 
  onNext, 
  navigationLabel = 'Week' 
}) => {
  if (isLoading) {
    return (
      <div className="chart-container">
        <div className="chart-loading">
          <div className="loading-spinner"></div>
          <p>Loading {title.toLowerCase()} data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="chart-container">
        <div className="chart-error">
          <div className="error-icon">!</div>
          <p>Error: {error}</p>
        </div>
      </div>
    );
  }

  if (noData) {
    return (
      <div className="chart-container">
        <div className="chart-no-data">
          <div className="no-data-icon">—</div>
          <p>No {title.toLowerCase()} data available</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chart-container">
      <div className="chart-header">
        <h3 className="chart-title">{title}</h3>
        <div className="chart-controls">
          {(onPrev && onNext) && (
            <div className="chart-navigation">
              <button 
                className="nav-button prev" 
                onClick={onPrev}
                aria-label={`Previous ${navigationLabel}`}
              >
                ‹
              </button>
              <button 
                className="nav-button next" 
                onClick={onNext}
                aria-label={`Next ${navigationLabel}`}
              >
                ›
              </button>
            </div>
          )}
          {onExpand && (
            <button onClick={onExpand} className="expand-button">
              {isExpanded ? 'Collapse' : 'Expand'}
            </button>
          )}
        </div>
      </div>
      <div className="chart-content">
        {children}
      </div>
    </div>
  );
};

export default ChartContainer;
