import React, { useState } from 'react';
import PatientSelector from './PatientSelector';
import usePatientData from './hooks/usePatientData';
import useVisualizations from './hooks/useVisualizations';
import useVisualizationHelpers from './hooks/useVisualizationHelpers';
import VisualizationWrapper from './components/VisualizationWrapper';
import './PhysicianDashboard.css';
import { PATIENTS } from './constants';

const PhysicianDashboard = ({ patientId: initialPatientId }) => {
  const [selectedPatientId, setSelectedPatientId] = useState(initialPatientId || '');
  const { data, loading, error } = usePatientData(selectedPatientId);

  // Update selected patient if prop changes
  React.useEffect(() => {
    if (initialPatientId && initialPatientId !== selectedPatientId) {
      setSelectedPatientId(initialPatientId);
    }
  }, [initialPatientId, selectedPatientId]);

  const { 
    allVisualizations, 
    availableVisualizations, 
    selectedVisualizations, 
    handleVisualizationChange 
  } = useVisualizations('physician', data);

  const { expandedItem, handleExpand, renderVisualization } = useVisualizationHelpers(
    selectedPatientId, 
    allVisualizations, 
    selectedVisualizations
  );

  // Override renderVisualization to set correct viewMode and handle no patient case
  const renderVisualizationWithMode = (visualizationType, windowId) => {
    if (!selectedPatientId) {
      return <div className="chart-placeholder">Please select a patient to view data.</div>;
    }

    const viz = allVisualizations[visualizationType];
    if (!viz) return <div className="chart-placeholder">Invalid visualization</div>;

    const Component = viz.component;
    const isExpanded = expandedItem === windowId;
    return <Component 
      patientId={selectedPatientId} 
      viewMode="physician"
      isExpanded={isExpanded}
      onExpand={() => handleExpand(windowId)}
    />;
  };

  const patientInfo = data?.patientInfo;

  return (
    <div className="physician-dashboard">
      <div className="physician-header">
        <h1>Physician Dashboard</h1>
        <PatientSelector
          patients={PATIENTS}
          value={selectedPatientId}
          onChange={setSelectedPatientId}
        />
      </div>

      {selectedPatientId && (
        <div className="dashboard-content">
          <div className="patient-info-card-physician">
            <h3>Patient Information</h3>
            {loading && <p>Loading patient info...</p>}
            {error && <p>Error loading data.</p>}
            {patientInfo && (
              <>
                <div><span>Patient #:</span> {patientInfo.patientNumber}</div>
                <div><span>Age:</span> {patientInfo.age} years</div>
                <div><span>Gender:</span> {patientInfo.gender || 'Not specified'}</div>
                <div className="conditions-section">
                  <span>Conditions:</span>
                  <div className="conditions-list">
                    {patientInfo.conditions?.map((condition, i) => (
                      <div key={i} className="condition-item">{condition}</div>
                    ))}
                  </div>
                </div>
                <div className="medications-section">
                  <span>Medications:</span>
                  <div className="medications-list">
                    {patientInfo.detailedMedications?.map((med, i) => (
                      <div key={i} className="medication-item">{med.name}</div>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>

          {expandedItem ? (
            <VisualizationWrapper
              id={expandedItem}
              className="physician-chart-container expanded-view"
              selectedViz={selectedVisualizations[expandedItem]}
              availableVisualizations={availableVisualizations}
              allVisualizations={allVisualizations}
              onVisualizationChange={handleVisualizationChange}
              onExpand={handleExpand}
              isExpanded={true}
              renderVisualization={renderVisualizationWithMode}
              disabled={Object.keys(availableVisualizations).length === 0}
              placeholderText="No data available for this patient."
            />
          ) : (
            <div className="physician-charts-grid">
              <VisualizationWrapper
                id="window1"
                className="physician-chart-container window-1"
                selectedViz={selectedVisualizations.window1}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window1'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
              <VisualizationWrapper
                id="window2"
                className="physician-chart-container window-2"
                selectedViz={selectedVisualizations.window2}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window2'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
              <VisualizationWrapper
                id="window3"
                className="physician-chart-container window-3"
                selectedViz={selectedVisualizations.window3}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window3'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
              <VisualizationWrapper
                id="window4"
                className="physician-chart-container window-4"
                selectedViz={selectedVisualizations.window4}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window4'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
              <VisualizationWrapper
                id="window5"
                className="physician-chart-container window-5"
                selectedViz={selectedVisualizations.window5}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window5'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
              <VisualizationWrapper
                id="window6"
                className="physician-chart-container window-6"
                selectedViz={selectedVisualizations.window6}
                availableVisualizations={availableVisualizations}
                allVisualizations={allVisualizations}
                onVisualizationChange={handleVisualizationChange}
                onExpand={handleExpand}
                isExpanded={expandedItem === 'window6'}
                renderVisualization={renderVisualizationWithMode}
                disabled={Object.keys(availableVisualizations).length === 0}
                placeholderText="No data available for this patient."
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PhysicianDashboard;
