/**
 * Factory for creating chart configurations
 * Eliminates duplicate getChartConfig functions across chart components
 */
export const createChartConfig = (type, isExpanded, containerWidth = 400) => {
  const baseConfig = {
    expanded: isExpanded,
    containerWidth
  };

  switch (type) {
    case 'glucose':
      return {
        width: isExpanded ? 480 : 400,
        height: isExpanded ? 380 : 360,
        padding: { top: 70, right: 30, bottom: 60, left: 50 },
        dayWidth: isExpanded ? 54 : 45,
        yAxisLabels: [0, 4, 8, 12],
        yAxisRange: 12,
        fontSize: {
          yAxis: 12,
          yAxisTitle: 14,
          timeLabel: 30,
          dayLabel: 30,
          dateLabel: 30,
        },
        barWidth: isExpanded ? 8 : 6,
      };

    case 'bloodPressure':
      const minWidth = 320;
      const maxWidth = isExpanded ? Math.min(700, containerWidth * 0.6) : 600;
      const responsiveWidth = Math.max(minWidth, Math.min(maxWidth, containerWidth || 400));
      
      return {
        width: responsiveWidth,
        height: isExpanded ? 320 : 180,
        padding: {
          top: 40,
          right: 20,
          bottom: 60,
          left: isExpanded ? 70 : 60,
        },
        yAxisRange: { systolic: 200, diastolic: 120 },
        yAxisOffset: { systolic: 0, diastolic: 0 },
        fontSize: {
          yAxis: isExpanded ? 14 : 12,
          yAxisTitle: isExpanded ? 16 : 14,
          timeLabel: isExpanded ? 10 : 8,
          dayLabel: isExpanded ? 10 : 8,
          dateLabel: isExpanded ? 10 : 8,
        },
        yAxisLabels: {
          systolic: isExpanded ? [0, 40, 80, 120, 160, 200] : [0, 100, 200],
          diastolic: isExpanded ? [0, 20, 40, 60, 80, 100, 120] : [0, 60, 120],
        },
        dayWidth: (responsiveWidth - (isExpanded ? 70 : 60) - 20) / 7,
        barWidth: isExpanded ? 12 : 6,
        dayPadding: isExpanded ? 8 : 4,
      };

    case 'exercise':
      return {
        width: isExpanded ? 700 : 320,
        height: isExpanded ? 245 : 180,
        padding: { top: 30, right: 10, bottom: 60, left: 50 },
        dayWidth: isExpanded ? 90 : 38,
        barWidth: isExpanded ? 25 : 20,
        fontSize: {
          yAxis: isExpanded ? 8 : 6,
          yAxisTitle: isExpanded ? 9 : 8,
          dayLabel: isExpanded ? 8 : 6,
          dateLabel: isExpanded ? 7 : 5,
          emoji: isExpanded ? 10 : 8,
        },
      };

    case 'mealContents':
      // Calculate responsive dimensions based on container width
      const mealMaxWidth = Math.min(containerWidth - 40, isExpanded ? 700 : 500); // Account for padding
      
      // Calculate day width based on available space
      const mealAvailableWidth = mealMaxWidth - 160; // Account for left padding (130) + right padding (30)
      
      // Ensure minimum viable width for the chart
      let mealDayWidth, mealTotalWidth, mealPadding;
      
      if (mealAvailableWidth < 420) { // 7 days * 60px minimum
        // For very small containers, reduce padding to fit content
        const adjustedPadding = Math.max(100, mealAvailableWidth * 0.2); // At least 100px padding for "Breakfast"
        const adjustedAvailableWidth = mealAvailableWidth - (160 - adjustedPadding);
        mealDayWidth = Math.max(50, adjustedAvailableWidth / 7);
        mealTotalWidth = adjustedPadding + (mealDayWidth * 7);
        mealPadding = { top: 40, right: 20, bottom: 80, left: adjustedPadding };
      } else {
        mealDayWidth = Math.max(60, Math.min(85, mealAvailableWidth / 7)); // Min 60px to accommodate emojis and circles
        mealTotalWidth = 160 + (mealDayWidth * 7);
        mealPadding = { top: 40, right: 30, bottom: 80, left: 130 };
      }
      
      // Calculate proper height to accommodate all 4 meal rows
      const mealHeight = isExpanded ? 80 : 60;
      const totalMealHeight = 4 * mealHeight; // 4 meal types
      const totalPadding = mealPadding.top + mealPadding.bottom;
      const mealMaxHeight = totalMealHeight + totalPadding;
      
      return {
        width: mealTotalWidth,
        height: mealMaxHeight,
        padding: mealPadding,
        dayWidth: mealDayWidth,
        mealHeight: mealHeight,
        fontSize: {
          dayLabel: isExpanded ? 10 : 8,
          dateLabel: isExpanded ? 8 : 6,
          mealLabel: isExpanded ? 8 : 6,
          timeLabel: isExpanded ? 6 : 5,
        },
      };

    default:
      return {
        width: isExpanded ? 480 : 400,
        height: isExpanded ? 320 : 280,
        padding: { top: 50, right: 30, bottom: 60, left: 50 },
        dayWidth: isExpanded ? 54 : 45,
        yAxisLabels: [0, 25, 50, 75, 100],
        yAxisRange: 100,
        fontSize: {
          yAxis: 12,
          yAxisTitle: 14,
          timeLabel: 10,
          dayLabel: 10,
          dateLabel: 10,
        },
        barWidth: isExpanded ? 8 : 6,
      };
  }
};
