/**
 * chartConstants.js - Shared Constants for Chart Components
 * 
 * This file consolidates common constants used across multiple chart components
 * to eliminate code duplication and ensure consistency.
 */

// Common time and date constants
export const DAYS_OF_WEEK = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
export const TIME_LABELS = ['12a', '12p', '12a'];
export const TIME_LABELS_12HOUR = ['12am', '12pm', '12am'];
export const DEFAULT_WEEK = new Date(2025, 4, 1);

// Common color schemes
export const RANGE_COLORS = {
  'below range': 'var(--chart-color-yellow)',
  'in range': 'var(--chart-color-blue)',
  'above range': 'var(--chart-color-orange)',
};

// Common date formatting functions
export const formatDayLabel = (date) => {
  return DAYS_OF_WEEK[date.getDay()];
};

export const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day;
  return new Date(d.setDate(diff));
};

// Common time positioning function
export const getTimePosition = (date, dayIndex, config, use12Hour = false) => {
  const hour = date.getHours() + date.getMinutes() / 60;
  const timeRatio = hour / 24;
  const dayContentWidth = config.dayWidth - (config.dayPadding || 0) * 2;
  return config.padding.left + (dayIndex * config.dayWidth) + (config.dayPadding || 0) + (timeRatio * dayContentWidth);
};
