import React, { useState, useMemo } from 'react';
import usePatientData from '../../hooks/usePatientData';
import ChartContainer from '../ChartContainer';
import Legend from '../Legend';
import '../ChartStyles.css';
import BodySVG from './BodySvg';

const PainChart = ({ patientId, isExpanded = false, onExpand, viewMode = 'patient' }) => {
  const { painData, isLoading: loading, error } = usePatientData(patientId, 'pain');
  const [currentWeek, setCurrentWeek] = useState(new Date(2025, 4, 1)); // Default to May 2025

  const handlePrevWeek = () => {
    setCurrentWeek(new Date(currentWeek.setDate(currentWeek.getDate() - 7)));
  };

  const handleNextWeek = () => {
    setCurrentWeek(new Date(currentWeek.setDate(currentWeek.getDate() + 7)));
  };

  const getPainColor = (level) => {
    if (level === 0) return '#E8F5E8'; // Very light green for no pain
    if (level <= 2) return viewMode === 'patient' ? 'var(--chart-color-good)' : '#FFE4B5'; // Green for mild pain
    if (level <= 4) return viewMode === 'patient' ? 'var(--chart-color-warning)' : '#FFB347'; // Orange for moderate pain
    if (level <= 6) return '#FF8C69'; // Dark orange for moderate-severe pain
    if (level <= 8) return viewMode === 'patient' ? 'var(--chart-color-danger)' : '#FF6347'; // Red for severe pain
    return '#DC143C'; // Dark red for extreme pain
  };

  const getPainIntensity = (level) => {
    if (level === 0) return 'No Pain';
    return `Rating ${level}`;
  };

  const painLegendItems = Array.from({ length: 11 }, (_, i) => i).map(level => ({
    color: getPainColor(level),
    label: `${level}`,
  }));

  // Calculate week dates
  const startOfWeek = new Date(currentWeek);
  startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay());
  startOfWeek.setHours(0, 0, 0, 0);

  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(endOfWeek.getDate() + 6);
  endOfWeek.setHours(23, 59, 59, 999);

  const weekPainData = painData.filter(d => d.date >= startOfWeek && d.date <= endOfWeek);

  // Process data for chart
  const { chartData, mostCommonLocation, averagePainLevel } = useMemo(() => {
    if (!weekPainData.length) return { chartData: [], mostCommonLocation: null, averagePainLevel: 0 };

    // Create a map of dates to pain levels
    const dateMap = new Map();
    const locationCounts = new Map();

    weekPainData.forEach(item => {
      const dateKey = item.date.toDateString();
      dateMap.set(dateKey, item.level);
      
      // Count locations
      const location = item.location.toLowerCase();
      locationCounts.set(location, (locationCounts.get(location) || 0) + 1);
    });

    // Find most common location
    let mostCommon = null;
    let maxCount = 0;
    for (const [location, count] of locationCounts) {
      if (count > maxCount) {
        maxCount = count;
        mostCommon = location;
      }
    }

    // Generate chart data for all 7 days of the week
    const data = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(date.getDate() + i);
      const dateKey = date.toDateString();
      const painLevel = dateMap.get(dateKey) || 0;
      
      data.push({
        date,
        day: date.toLocaleDateString('en-US', { weekday: 'short' }),
        level: painLevel,
        color: getPainColor(painLevel)
      });
    }

    const totalPain = weekPainData.reduce((sum, item) => sum + item.level, 0);
    const avgPain = weekPainData.length > 0 ? (totalPain / weekPainData.length) : 0;

    return {
      chartData: data,
      mostCommonLocation: mostCommon,
      averagePainLevel: avgPain
    };
  }, [weekPainData, startOfWeek]);

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  const Body = ({ location, level }) => {
    const color = getPainColor(level);
    const locationLower = location.toLowerCase();

    return (
      <div className="body-container">
        <div className="body-views">
          <div className="body-view">
            <div className="body-view-label">Front</div>
            <BodySVG 
              className="body-svg" 
              painLocation={locationLower} 
              painColor={color} 
              view="front" 
            />
          </div>
          <div className="body-view">
            <div className="body-view-label">Back</div>
            <BodySVG 
              className="body-svg" 
              painLocation={locationLower} 
              painColor={color} 
              view="back" 
            />
          </div>
        </div>
      </div>
    );
  };

  if (viewMode === 'patient') {
    return (
      <ChartContainer
        title="Pain Report"
        icon="ðŸ¤•"
        isLoading={loading}
        error={error}
        noData={!loading && !error && (!painData || painData.length === 0)}
        isExpanded={isExpanded}
        onExpand={onExpand}
        onPrev={handlePrevWeek}
        onNext={handleNextWeek}
        navigationLabel={formatDateRange(startOfWeek, endOfWeek)}
      >
        <div className="pain-chart-container">
          <div className="pain-legend-wrapper">
            <Legend title="Pain Intensity Scale (0-10)" items={painLegendItems} />
          </div>
          <div className="pain-chart-wrapper">
            <div className="pain-list">
              {weekPainData.length > 0 ? (
                weekPainData.map((item, index) => (
                  <div key={index} className="pain-list-item">
                    <div className="pain-item-date">
                      {new Date(item.date).toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}
                    </div>
                    <div className="pain-item-body">
                      <Body location={item.location} level={item.level} />
                    </div>
                    <div className="pain-item-details">
                      <div className="pain-level">Level: {item.level}/10</div>
                      <div className="pain-location">Location: {item.location}</div>
                      <div className="pain-intensity">{getPainIntensity(item.level)}</div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-pain-data">No pain data available for this week</div>
              )}
            </div>
          </div>
        </div>
      </ChartContainer>
    );
  }

  // Physician view
  return (
    <>
      <div className="pain-legend-wrapper">
        <Legend title="Pain Intensity Scale (0-10)" items={painLegendItems} />
      </div>
      
      <ChartContainer
        title="Pain Report 2"
        icon="ðŸ¤•"
        isLoading={loading}
        error={error}
        noData={!loading && !error && (!painData || painData.length === 0)}
        isExpanded={isExpanded}
        onExpand={onExpand}
        onPrev={handlePrevWeek}
        onNext={handleNextWeek}
        navigationLabel={formatDateRange(startOfWeek, endOfWeek)}
      >
        <div className="physician-pain-chart-container">
          <div className="pain-details">
          <h4>Daily Pain Details</h4>
          <div className="pain-details-list">
            {weekPainData.length > 0 ? (
              weekPainData.map((item, index) => (
                <div key={index} className="pain-detail-item">
                  <div className="detail-date">
                    {new Date(item.date).toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}
                  </div>
                  <div className="detail-body">
                    <Body location={item.location} level={item.level} />
                  </div>
                  <div className="detail-info">
                    <div className="detail-level">Level: {item.level}/10</div>
                    <div className="detail-location">Location: {item.location}</div>
                    <div className="detail-intensity">{getPainIntensity(item.level)}</div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-pain-data">No pain data available for this week</div>
            )}
          </div>
        </div>

        <div className="pain-summary">
          <div className="summary-item">
            <span className="summary-label">Average Pain Level:</span>
            <span className="summary-value">{averagePainLevel.toFixed(1)}/10</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Most Common Location:</span>
            <span className="summary-value">{mostCommonLocation || 'N/A'}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Days with Pain:</span>
            <span className="summary-value">{weekPainData.length}/7</span>
          </div>
        </div>
      </div>
      </ChartContainer>
    </>
  );
};

export default PainChart;
