import React from 'react';
import './Switch.css';

const Switch = ({ 
  checked, 
  onChange, 
  leftLabel = 'Patient View', 
  rightLabel = 'Physician View',
  className = '',
  disabled = false 
}) => {
  return (
    <div className={`switch-container ${className}`}>
      <label className="switch-label">
        <input
          type="checkbox"
          checked={checked}
          onChange={onChange}
          disabled={disabled}
          aria-label="Toggle between patient and physician view"
        />
        <span className="switch-slider"></span>
        <div className="switch-labels">
          <span className={`switch-label-left ${!checked ? 'active' : ''}`}>
            {leftLabel}
          </span>
          <span className={`switch-label-right ${checked ? 'active' : ''}`}>
            {rightLabel}
          </span>
        </div>
      </label>
    </div>
  );
};

export default Switch;
