/**
 * VisualizationWrapper.js - Universal Chart Container Component
 * 
 * This component provides a standardized wrapper for all chart visualizations:
 * - Handles loading, error, and no-data states
 * - Provides chart navigation controls (previous/next)
 * - Manages expand/collapse functionality
 * - Displays chart titles and controls
 * - Ensures consistent chart presentation across the application
 * 
 * Serves as the foundation for all health metric chart displays.
 */

import React from 'react';

/**
 * Shared component for visualization containers used in both Patient and Physician dashboards
 * Now includes functionality previously in ChartContainer: navigation, loading states, error handling
 */
const VisualizationWrapper = ({ 
  id, 
  className, 
  selectedViz, 
  availableVisualizations, 
  allVisualizations,
  onVisualizationChange, 
  onExpand, 
  isExpanded, 
  renderVisualization,
  disabled = false,
  placeholderText = "No data available for this patient.",
  // ChartContainer props
  title,
  isLoading = false,
  error = null,
  noData = false,
  showNavigation = true,
  onPrev,
  onNext,
  navigationLabel = 'Week'
}) => {
  const viz = allVisualizations[selectedViz];

  // Handle loading state
  if (isLoading) {
    return (
      <div className={`visualization-wrapper ${isExpanded ? 'expanded' : ''} ${className}`}>
        <div className="chart-loading">
          <div className="loading-spinner"></div>
          <p>Loading data...</p>
        </div>
      </div>
    );
  }

  // Handle error state
  if (error) {
    return (
      <div className={`visualization-wrapper ${isExpanded ? 'expanded' : ''} ${className}`}>
        <div className="chart-error">
          <div className="error-icon">!</div>
          <p>Error: {error}</p>
        </div>
      </div>
    );
  }

  // Handle no data state
  if (noData) {
    return (
      <div className={`visualization-wrapper ${isExpanded ? 'expanded' : ''} ${className}`}>
        <div className="chart-no-data">
          <div className="no-data-icon">—</div>
          <p>No data available</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`visualization-wrapper ${isExpanded ? 'expanded' : ''} ${className}`}>
      <div className="visualization-header">
        <div className="visualization-title-group">
          <h3 className="visualization-title" style={{ color: viz?.color || '#2c3e50' }}>
            {title || viz?.name || 'Unknown Visualization'}
          </h3>
        </div>
        <div className="visualization-controls">
          {showNavigation && onPrev && onNext && (
            <div className="chart-navigation">
              <button 
                className="nav-button prev" 
                onClick={onPrev}
                aria-label={`Previous ${navigationLabel}`}
              >
                ‹
              </button>
              <button 
                className="nav-button next" 
                onClick={onNext}
                aria-label={`Next ${navigationLabel}`}
              >
                ›
              </button>
            </div>
          )}
          <button onClick={() => onExpand(id)} className="expand-button">
            {isExpanded ? 'Collapse' : 'Expand'}
          </button>
        </div>
      </div>
      <div className="visualization-content">
        {Object.keys(availableVisualizations).length === 0 ? (
          <div className="chart-placeholder">{placeholderText}</div>
        ) : (
          renderVisualization(selectedViz, id)
        )}
      </div>
    </div>
  );
};

export default VisualizationWrapper;
