/**
 * PhysicianExerciseChart.js - Physician-Specific Exercise Visualization
 * 
 * This component provides a simplified exercise overview for physicians with three key categories:
 * - Aerobic: 150 minutes/week goal
 * - Strength: 2+ sessions/week (10+ minutes each) goal  
 * - Flexibility/Balance: 1+ session/week goal
 * 
 * Features:
 * - Three-bar chart showing progress toward weekly goals
 * - Color-coded completion percentages
 * - Clear goal indicators and current progress
 * - Physician-focused summary statistics
 * 
 * Architecture:
 * - Uses custom SVG for clean bar chart visualization
 * - Implements goal-based progress tracking
 * - Provides clinical summary for physician decision making
 * - Supports expandable views for detailed analysis
 */

import React, { useMemo } from 'react';
import usePatientData from '../../hooks/usePatientData';
import useChartNavigation from '../../hooks/useChartNavigation';
import { createChartConfig } from '../chart-utils/ChartConfigFactory';
import './PhysicianExerciseChart.css';

const PhysicianExerciseChart = ({ patientId, isExpanded = false, onExpand, viewMode = 'physician', navigation }) => {
  const { exerciseData, loading, error } = usePatientData(patientId, 'exercise');
  
  // Use navigation from parent or fallback to internal navigation
  const useInternalNavigation = !navigation;
  const internalNavigation = useChartNavigation('exercise');
  const nav = navigation || internalNavigation;

  const config = createChartConfig('exercise', isExpanded);
  const chartHeight = config.height - config.padding.top - config.padding.bottom;

  const { start: startOfWeek, end: endOfWeek } = nav.getDateRange();

  // Filter data for current week
  const weekData = exerciseData.filter(d => {
    if (!d.date) return false;
    const itemDate = new Date(d.date);
    return itemDate >= startOfWeek && itemDate <= endOfWeek;
  });

  // Calculate exercise categories and progress
  const exerciseProgress = useMemo(() => {
    if (!weekData.length) {
      return {
        aerobic: { minutes: 0, goal: 150, percentage: 0, sessions: 0 },
        strength: { sessions: 0, goal: 2, percentage: 0, totalMinutes: 0 },
        flexibility: { sessions: 0, goal: 1, percentage: 0, totalMinutes: 0 }
      };
    }

    // Calculate aerobic minutes (walking, swimming, running, biking)
    const aerobicMinutes = weekData.reduce((total, day) => {
      return total + (Number(day.walking) || 0) + (Number(day.swimming) || 0) + 
             (Number(day.running) || 0) + (Number(day.biking) || 0);
    }, 0);

    // Calculate strength sessions (resistance/weights with 10+ minutes)
    const strengthSessions = weekData.filter(day => 
      (Number(day['resistance/weights']) || 0) >= 10
    ).length;
    const strengthMinutes = weekData.reduce((total, day) => 
      total + (Number(day['resistance/weights']) || 0), 0
    );

    // Calculate flexibility/balance sessions (balance/stretching with any duration)
    const flexibilitySessions = weekData.filter(day => 
      (Number(day['balance/stretching']) || 0) > 0
    ).length;
    const flexibilityMinutes = weekData.reduce((total, day) => 
      total + (Number(day['balance/stretching']) || 0), 0
    );

    return {
      aerobic: {
        minutes: aerobicMinutes,
        goal: 150,
        percentage: Math.min(100, Math.round((aerobicMinutes / 150) * 100)),
        sessions: weekData.filter(day => 
          (Number(day.walking) || 0) + (Number(day.swimming) || 0) + 
          (Number(day.running) || 0) + (Number(day.biking) || 0) > 0
        ).length
      },
      strength: {
        sessions: strengthSessions,
        goal: 2,
        percentage: Math.min(100, Math.round((strengthSessions / 2) * 100)),
        totalMinutes: strengthMinutes
      },
      flexibility: {
        sessions: flexibilitySessions,
        goal: 1,
        percentage: Math.min(100, Math.round((flexibilitySessions / 1) * 100)),
        totalMinutes: flexibilityMinutes
      }
    };
  }, [weekData]);

  // Define categories with colors and goals
  const categories = [
    {
      key: 'aerobic',
      label: 'Aerobic Activity',
      emoji: 'üèÉ',
      color: '#1f78b4', // Blue
      goal: '150 min/week',
      current: `${exerciseProgress.aerobic.minutes} min`,
      percentage: exerciseProgress.aerobic.percentage
    },
    {
      key: 'strength',
      label: 'Strength Training',
      emoji: 'üèãÔ∏è',
      color: '#d95f02', // Orange
      goal: '2+ sessions/week',
      current: `${exerciseProgress.strength.sessions} sessions`,
      percentage: exerciseProgress.strength.percentage
    },
    {
      key: 'flexibility',
      label: 'Flexibility/Balance',
      emoji: 'üßò',
      color: '#1b9e77', // Green
      goal: '1+ session/week',
      current: `${exerciseProgress.flexibility.sessions} sessions`,
      percentage: exerciseProgress.flexibility.percentage
    }
  ];

  const maxPercentage = 100;
  const barWidth = 80;
  const barSpacing = 40;
  const startX = config.padding.left + 50;

  return (
    <div className={`physician-exercise-chart-container ${isExpanded ? 'expanded' : ''}`}>
      <h3 className="chart-title">Weekly Exercise Goals Progress</h3>
      <h4 className="chart-subtitle">{nav.getCurrentMonthYear()}</h4>
      
      <svg 
        width="100%" 
        viewBox={`0 0 ${config.width} ${config.height}`}
        className="physician-exercise-svg"
      >
        {/* Y-axis */}
        <g className="y-axis">
          <line 
            x1={config.padding.left} 
            y1={config.padding.top} 
            x2={config.padding.left} 
            y2={config.height - config.padding.bottom} 
            stroke="var(--chart-color-text-secondary)" 
            strokeWidth="1"
          />
          {[0, 25, 50, 75, 100].map(value => {
            const y = config.padding.top + chartHeight - (value / maxPercentage) * chartHeight;
            return (
              <g key={value} className="y-axis-grid-group">
                <line 
                  className="chart-grid-line-horizontal" 
                  x1={config.padding.left} 
                  y1={y} 
                  x2={config.width - config.padding.right} 
                  y2={y} 
                />
                <text 
                  x={config.padding.left - 10} 
                  y={y + 3} 
                  fontSize={config.fontSize.yAxis} 
                  textAnchor="end" 
                  fill="var(--chart-color-text-secondary)" 
                  className="chart-tick-label"
                >
                  {value}%
                </text>
              </g>
            );
          })}
          <text 
            x={-(config.padding.top + chartHeight / 2)} 
            y={config.padding.left / 2 - 5} 
            fontSize={config.fontSize.yAxisTitle} 
            textAnchor="middle" 
            fill="var(--chart-color-text-primary)" 
            transform="rotate(-90)" 
            className="y-axis-title"
          >
            Goal Completion
          </text>
        </g>

        {/* X-axis */}
        <g className="x-axis">
          {categories.map((category, index) => {
            const x = startX + index * (barWidth + barSpacing);
            return (
              <g key={category.key} className="x-axis-label-group">
                <text 
                  x={x} 
                  y={config.height - config.padding.bottom + 20} 
                  textAnchor="middle" 
                  fontSize={config.fontSize.dayLabel} 
                  fill="var(--chart-color-text-primary)" 
                  className="x-axis-category-label"
                >
                  {category.emoji}
                </text>
                <text 
                  x={x} 
                  y={config.height - config.padding.bottom + 35} 
                  textAnchor="middle" 
                  fontSize={config.fontSize.dateLabel} 
                  fill="var(--chart-color-text-primary)" 
                  className="x-axis-category-name"
                >
                  {category.label}
                </text>
              </g>
            );
          })}
        </g>

        {/* Data bars */}
        <g className="data-bars">
          {categories.map((category, index) => {
            const x = startX + index * (barWidth + barSpacing);
            const barHeight = (category.percentage / maxPercentage) * chartHeight;
            const barY = config.height - config.padding.bottom - barHeight;
            
            // Determine bar color based on completion
            let barColor = category.color;
            if (category.percentage >= 100) {
              barColor = '#27ae60'; // Green for completed
            } else if (category.percentage >= 75) {
              barColor = '#f39c12'; // Orange for good progress
            } else if (category.percentage >= 50) {
              barColor = '#e67e22'; // Dark orange for moderate progress
            } else {
              barColor = '#e74c3c'; // Red for low progress
            }

            return (
              <g key={category.key}>
                {/* Background bar (goal line) */}
                <rect
                  x={x - barWidth / 2}
                  y={config.padding.top}
                  width={barWidth}
                  height={chartHeight}
                  fill="var(--chart-color-background)"
                  stroke="var(--chart-color-text-secondary)"
                  strokeWidth="1"
                  strokeDasharray="2,2"
                  className="goal-background-bar"
                />
                
                {/* Progress bar */}
                <rect
                  x={x - barWidth / 2}
                  y={barY}
                  width={barWidth}
                  height={barHeight}
                  fill={barColor}
                  className="progress-bar"
                >
                  <title>
                    {category.label}: {category.percentage}% complete
                    {category.key === 'aerobic' && ` (${category.current} of ${category.goal})`}
                    {category.key === 'strength' && ` (${category.current} of ${category.goal})`}
                    {category.key === 'flexibility' && ` (${category.current} of ${category.goal})`}
                  </title>
                </rect>

                {/* Percentage label */}
                <text
                  x={x}
                  y={barY - 5}
                  textAnchor="middle"
                  fontSize={isExpanded ? 12 : 10}
                  fill="var(--chart-color-text-primary)"
                  className="percentage-label"
                  fontWeight="bold"
                >
                  {category.percentage}%
                </text>

                {/* Current value label */}
                <text
                  x={x}
                  y={config.height - config.padding.bottom + 50}
                  textAnchor="middle"
                  fontSize={isExpanded ? 10 : 8}
                  fill="var(--chart-color-text-secondary)"
                  className="current-value-label"
                >
                  {category.current}
                </text>
              </g>
            );
          })}
        </g>
      </svg>

      {/* Summary for physician */}
      <div className="physician-exercise-summary">
        <h4>Exercise Summary</h4>
        <div className="summary-stats">
          <div className="stat-item">
            <span className="stat-label">Aerobic Progress:</span>
            <span className="stat-value">
              {exerciseProgress.aerobic.minutes}/150 minutes ({exerciseProgress.aerobic.percentage}%)
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Strength Sessions:</span>
            <span className="stat-value">
              {exerciseProgress.strength.sessions}/2 sessions ({exerciseProgress.strength.percentage}%)
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Flexibility Sessions:</span>
            <span className="stat-value">
              {exerciseProgress.flexibility.sessions}/1 session ({exerciseProgress.flexibility.percentage}%)
            </span>
          </div>
        </div>
        
        {/* Recommendations */}
        <div className="recommendations">
          <h5>Recommendations:</h5>
          <ul>
            {exerciseProgress.aerobic.percentage < 100 && (
              <li>Increase aerobic activity to reach 150 minutes/week goal</li>
            )}
            {exerciseProgress.strength.percentage < 100 && (
              <li>Add more strength training sessions (aim for 2+ per week)</li>
            )}
            {exerciseProgress.flexibility.percentage < 100 && (
              <li>Include flexibility/balance exercises at least once per week</li>
            )}
            {exerciseProgress.aerobic.percentage >= 100 && 
             exerciseProgress.strength.percentage >= 100 && 
             exerciseProgress.flexibility.percentage >= 100 && (
              <li>Excellent! All exercise goals are being met.</li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default PhysicianExerciseChart;
