/**
 * ExerciseProgressChart.js - Exercise Progress Visualization for Physician View
 * 
 * This component provides a physician-focused exercise progress visualization:
 * - Three exercise categories: Aerobic, Strength, and Flexibility/Balance
 * - Progress bars showing completion percentage against weekly goals
 * - Color-coded progress indicators (green for goal met, yellow for partial, red for not met)
 * - Weekly goal tracking with clear visual feedback
 * - Integration with patient data and chart navigation
 * 
 * Architecture:
 * - Uses custom SVG for progress bar visualization
 * - Implements goal-based progress tracking
 * - Provides color-coded progress indicators
 * - Supports expandable views
 * - Implements time-based navigation and data filtering
 * 
 * Exercise Categories:
 * - Aerobic: 150 minutes per week goal
 * - Strength: At least 2 sessions per week (minimum 10 minutes each)
 * - Flexibility/Balance: At least 1 session per week
 * 
 * Component Structure:
 * - Chart Container: Main SVG container with sizing
 * - Progress Bars: Three horizontal progress bars with percentage completion
 * - Goal Indicators: Clear goal targets and current progress
 * - Color Coding: Visual feedback for goal achievement
 * - Summary Statistics: Weekly totals and goal achievement status
 * 
 * Essential for physician exercise monitoring and goal tracking.
 */

import React, { useState, useMemo, useRef } from 'react';
import usePatientData from '../../hooks/usePatientData';
import useChartNavigation from '../../hooks/useChartNavigation';
import { createChartConfig } from '../chart-utils/ChartConfigFactory';
import './ExerciseProgressChart.css';

const ExerciseProgressChart = ({ patientId, isExpanded = false, onExpand, viewMode = 'physician', navigation }) => {
  const { exerciseData, loading, error } = usePatientData(patientId, 'exercise');
  
  // Use navigation from parent or fallback to internal navigation
  const useInternalNavigation = !navigation;
  const internalNavigation = useChartNavigation('exercise');
  const nav = navigation || internalNavigation;

  const config = createChartConfig('exercise', isExpanded);
  const chartHeight = config.height - config.padding.top - config.padding.bottom;

  const { start: startOfWeek, end: endOfWeek } = nav.getDateRange();

  const weekData = exerciseData.filter(d => {
    if (!d.date) return false;
    const itemDate = new Date(d.date);
    return itemDate >= startOfWeek && itemDate <= endOfWeek;
  });

  // Calculate exercise progress for the week
  const exerciseProgress = useMemo(() => {
    if (!weekData.length) {
      return {
        aerobic: { minutes: 0, goal: 150, percentage: 0, status: 'not-met' },
        strength: { sessions: 0, goal: 2, percentage: 0, status: 'not-met' },
        flexibility: { sessions: 0, goal: 1, percentage: 0, status: 'not-met' }
      };
    }

    // Calculate aerobic minutes (walking, swimming, running, biking)
    const aerobicMinutes = weekData.reduce((total, day) => {
      return total + (Number(day.walking) || 0) + (Number(day.swimming) || 0) + 
             (Number(day.running) || 0) + (Number(day.biking) || 0);
    }, 0);

    // Calculate strength sessions (resistance/weights with minimum 10 minutes)
    const strengthSessions = weekData.filter(day => {
      const strengthMinutes = Number(day['resistance/weights']) || 0;
      return strengthMinutes >= 10;
    }).length;

    // Calculate flexibility/balance sessions (balance/stretching with any duration)
    const flexibilitySessions = weekData.filter(day => {
      const flexibilityMinutes = Number(day['balance/stretching']) || 0;
      return flexibilityMinutes > 0;
    }).length;

    // Calculate percentages and status
    const aerobicPercentage = Math.min((aerobicMinutes / 150) * 100, 100);
    const strengthPercentage = Math.min((strengthSessions / 2) * 100, 100);
    const flexibilityPercentage = Math.min((flexibilitySessions / 1) * 100, 100);

    const getStatus = (percentage) => {
      if (percentage >= 100) return 'met';
      if (percentage >= 50) return 'partial';
      return 'not-met';
    };

    return {
      aerobic: {
        minutes: aerobicMinutes,
        goal: 150,
        percentage: aerobicPercentage,
        status: getStatus(aerobicPercentage)
      },
      strength: {
        sessions: strengthSessions,
        goal: 2,
        percentage: strengthPercentage,
        status: getStatus(strengthPercentage)
      },
      flexibility: {
        sessions: flexibilitySessions,
        goal: 1,
        percentage: flexibilityPercentage,
        status: getStatus(flexibilityPercentage)
      }
    };
  }, [weekData]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'met': return '#27ae60'; // Green
      case 'partial': return '#f39c12'; // Orange
      case 'not-met': return '#e74c3c'; // Red
      default: return '#bdc3c7'; // Gray
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'met': return 'Goal Met';
      case 'partial': return 'Partial';
      case 'not-met': return 'Not Met';
      default: return 'Unknown';
    }
  };

  const categories = [
    {
      key: 'aerobic',
      label: 'Aerobic Activity',
      emoji: 'üèÉ‚Äç‚ôÇÔ∏è',
      progress: exerciseProgress.aerobic,
      description: '150 minutes per week'
    },
    {
      key: 'strength',
      label: 'Strength Training',
      emoji: 'üèãÔ∏è',
      progress: exerciseProgress.strength,
      description: '2+ sessions per week (10+ min each)'
    },
    {
      key: 'flexibility',
      label: 'Flexibility/Balance',
      emoji: 'üßò',
      progress: exerciseProgress.flexibility,
      description: '1+ session per week'
    }
  ];

  const barHeight = 40;
  const barSpacing = 60;
  const startY = config.padding.top + 50;

  return (
    <div className={`exercise-progress-chart-container ${isExpanded ? 'expanded' : ''}`}>
      <h3 className="chart-title">Exercise Progress</h3>
      <h4 className="chart-subtitle">{nav.getCurrentMonthYear()}</h4>
      
      <svg 
        width="100%" 
        viewBox={`0 0 ${config.width} ${config.height}`}
        className="exercise-progress-svg"
      >
        {/* Background bars */}
        {categories.map((category, index) => {
          const y = startY + index * barSpacing;
          const barWidth = config.width - config.padding.left - config.padding.right - 200;
          const progressWidth = (category.progress.percentage / 100) * barWidth;
          
          return (
            <g key={category.key} className="progress-bar-group">
              {/* Background bar */}
              <rect
                x={config.padding.left}
                y={y - barHeight / 2}
                width={barWidth}
                height={barHeight}
                fill="#ecf0f1"
                stroke="#bdc3c7"
                strokeWidth="1"
                rx="5"
                className="progress-background"
              />
              
              {/* Progress bar */}
              <rect
                x={config.padding.left}
                y={y - barHeight / 2}
                width={progressWidth}
                height={barHeight}
                fill={getStatusColor(category.progress.status)}
                rx="5"
                className="progress-fill"
              />
              
              {/* Category label */}
              <text
                x={config.padding.left - 10}
                y={y + 5}
                textAnchor="end"
                fontSize="14"
                fill="var(--chart-color-text-primary)"
                className="category-label"
              >
                {category.emoji} {category.label}
              </text>
              
              {/* Progress percentage */}
              <text
                x={config.padding.left + barWidth + 10}
                y={y + 5}
                textAnchor="start"
                fontSize="12"
                fill="var(--chart-color-text-primary)"
                className="percentage-label"
              >
                {Math.round(category.progress.percentage)}%
              </text>
              
              {/* Status indicator */}
              <circle
                cx={config.padding.left + barWidth + 10}
                cy={y - 10}
                r="6"
                fill={getStatusColor(category.progress.status)}
                className="status-indicator"
              />
              
              {/* Current progress text */}
              <text
                x={config.padding.left + barWidth + 30}
                y={y - 5}
                textAnchor="start"
                fontSize="10"
                fill="var(--chart-color-text-secondary)"
                className="progress-text"
              >
                {category.key === 'aerobic' 
                  ? `${category.progress.minutes}/${category.progress.goal} min`
                  : `${category.progress.sessions}/${category.progress.goal} sessions`
                }
              </text>
              
              {/* Goal description */}
              <text
                x={config.padding.left + barWidth + 30}
                y={y + 8}
                textAnchor="start"
                fontSize="9"
                fill="var(--chart-color-text-secondary)"
                className="goal-description"
              >
                {category.description}
              </text>
            </g>
          );
        })}
      </svg>
      
      {/* Summary statistics */}
      <div className="exercise-summary">
        <div className="summary-stats">
          <div className="stat-item">
            <span className="stat-label">Weekly Goals Met:</span>
            <span className="stat-value">
              {Object.values(exerciseProgress).filter(p => p.status === 'met').length}/3
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Total Aerobic:</span>
            <span className="stat-value">
              {exerciseProgress.aerobic.minutes} minutes
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Strength Sessions:</span>
            <span className="stat-value">
              {exerciseProgress.strength.sessions} sessions
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Flexibility Sessions:</span>
            <span className="stat-value">
              {exerciseProgress.flexibility.sessions} sessions
            </span>
          </div>
        </div>
        
        {/* Status legend */}
        <div className="status-legend">
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#27ae60' }}></div>
            <span>Goal Met</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#f39c12' }}></div>
            <span>Partial</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#e74c3c' }}></div>
            <span>Not Met</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExerciseProgressChart;
