/**
 * ExerciseChart.js - Exercise Activity Tracking Visualization
 * 
 * This component provides comprehensive exercise monitoring:
 * - Activity type breakdown (walking, swimming, running, resistance, biking, balance)
 * - Duration tracking and weekly summaries
 * - Color-coded activity categorization
 * - Interactive tooltips with exercise details
 * - Navigation controls for time periods
 * - Integration with patient data and chart navigation
 * 
 * Architecture:
 * - Uses custom SVG for stacked bar chart visualization
 * - Implements activity categorization system with emoji indicators
 * - Provides color-coded activity types for easy identification
 * - Supports expandable views
 * - Implements time-based navigation and data filtering
 * 
 * Visualization Features:
 * - Stacked bar chart showing daily activity breakdown
 * - Color-coded activity types with emoji indicators
 * - Interactive tooltips with detailed exercise information
 * - Design adapting to container size
 * - Dynamic Y-axis scaling based on data range
 * 
 * Activity Categories:
 * - Walking: Low-impact cardiovascular exercise
 * - Swimming: Full-body cardiovascular workout
 * - Running: High-impact cardiovascular exercise
 * - Resistance/Weights: Strength training activities
 * - Biking: Low-impact cardiovascular exercise
 * - Balance/Stretching: Flexibility and balance training
 * - Other: Miscellaneous physical activities
 * 
 * Component Structure:
 * - Chart Container: Main SVG container with sizing
 * - Y-Axis: Duration scale with dynamic labeling
 * - X-Axis: Day-of-week labels with date information
 * - Data Bars: Stacked activity bars with color coding
 * - Legend: Activity type explanations with emojis
 * - Tooltip: Detailed exercise information on hover
 * 
 * Essential for physical activity monitoring and fitness tracking.
 */

import React, { useState, useMemo, useRef } from 'react';
import usePatientData from '../../hooks/usePatientData';
import useChartNavigation from '../../hooks/useChartNavigation';
import Legend from '../Legend';
import InfoBox from '../InfoBox';
import { createChartConfig } from '../chart-utils/ChartConfigFactory';
import './ExerciseChart.css';

const exerciseTypes = [
  { key: 'walking', label: 'Walking', color: 'var(--chart-color-walking)', emoji: 'ðŸš¶' },
  { key: 'swimming', label: 'Swimming', color: 'var(--chart-color-swimming)', emoji: 'ðŸŠâ€â™€ï¸' },
  { key: 'running', label: 'Running', color: 'var(--chart-color-running)', emoji: 'ðŸƒâ€â™‚ï¸' },
  { key: 'resistance/weights', label: 'Resistance/Weights', color: 'var(--chart-color-resistance)', emoji: 'ðŸ‹ï¸' },
  { key: 'biking', label: 'Biking', color: 'var(--chart-color-biking)', emoji: 'ðŸš´' },
  { key: 'balance/stretching', label: 'Balance/Stretching', color: 'var(--chart-color-balance)', emoji: 'ðŸ§˜' },
  { key: 'other', label: 'Other', color: 'var(--chart-color-other)', emoji: 'âž•' },
];

// Exercise goals for physician view
const EXERCISE_GOALS = {
  aerobic: {
    target: 150, // minutes per week
    label: 'Aerobic Activity',
    color: '#1f78b4',
    description: '150 minutes/week'
  },
  strength: {
    target: 2, // sessions per week (minimum 10 minutes each)
    label: 'Strength Training',
    color: '#e31a1c',
    description: '2 sessions/week (10+ min each)'
  },
  flexibility: {
    target: 1, // session per week
    label: 'Flexibility/Balance',
    color: '#33a02c',
    description: '1 session/week'
  }
};

const ExerciseChart = ({ patientId, isExpanded = false, onExpand, viewMode = 'patient', navigation }) => {
  const { exerciseData, loading, error } = usePatientData(patientId, 'exercise');
  
  // Use navigation from parent or fallback to internal navigation
  const useInternalNavigation = !navigation;
  const internalNavigation = useChartNavigation('exercise');
  const nav = navigation || internalNavigation;

  const config = createChartConfig('exercise', isExpanded);
  const chartHeight = config.height - config.padding.top - config.padding.bottom;

  const { start: startOfWeek, end: endOfWeek } = nav.getDateRange();

  const weekData = exerciseData.filter(d => {
    if (!d.date) return false;
    const itemDate = new Date(d.date);
    return itemDate >= startOfWeek && itemDate <= endOfWeek;
  });

  // Get 3-month data
  const { start: startOfThreeMonths, end: endOfThreeMonths } = nav.getThreeMonthRange();
  const threeMonthData = exerciseData.filter(d => {
    if (!d.date) return false;
    const itemDate = new Date(d.date);
    return itemDate >= startOfThreeMonths && itemDate <= endOfThreeMonths;
  });

  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const groupedData = daysOfWeek.reduce((acc, day) => {
    acc[day] = { walking: 0, swimming: 0, running: 0, 'resistance/weights': 0, biking: 0, 'balance/stretching': 0, other: 0, totalMinutes: 0 };
    return acc;
  }, {});

  weekData.forEach(item => {
    if (!item.date) return;
    const dayName = daysOfWeek[new Date(item.date).getDay()];
    if (groupedData[dayName]) {
      groupedData[dayName].walking += Number(item.walking) || 0;
      groupedData[dayName].swimming += Number(item.swimming) || 0;
      groupedData[dayName].running += Number(item.running) || 0;
      groupedData[dayName]['resistance/weights'] += Number(item['resistance/weights']) || 0;
      groupedData[dayName].biking += Number(item.biking) || 0;
      groupedData[dayName]['balance/stretching'] += Number(item['balance/stretching']) || 0;
      groupedData[dayName].other += Number(item.other) || 0;
      groupedData[dayName].totalMinutes = groupedData[dayName].walking + groupedData[dayName].swimming + groupedData[dayName].running + groupedData[dayName]['resistance/weights'] + groupedData[dayName].biking + groupedData[dayName]['balance/stretching'] + groupedData[dayName].other;
    }
  });

  const maxVal = Math.max(...Object.values(groupedData).map(day => day.totalMinutes), 0);
  const maxDuration = Math.max(60, Math.ceil(maxVal / 30) * 30);

  const generateYAxisLabels = (max) => {
    if (max === 0) return [0];
    const step = Math.max(15, Math.ceil(max / 6 / 15) * 15);
    const labels = [];
    for (let i = 0; i <= max; i += step) {
      labels.push(i);
    }
    return labels;
  };

  const yAxisLabels = generateYAxisLabels(maxDuration);

  // Calculate exercise progress for physician view
  const exerciseProgress = useMemo(() => {
    if (!weekData.length) return null;

    // Calculate aerobic activity (walking, swimming, running, biking)
    const aerobicMinutes = Object.values(groupedData).reduce((sum, day) => 
      sum + day.walking + day.swimming + day.running + day.biking, 0
    );

    // Calculate strength training sessions (resistance/weights with 10+ minutes)
    const strengthSessions = Object.values(groupedData).filter(day => 
      day['resistance/weights'] >= 10
    ).length;

    // Calculate flexibility/balance sessions
    const flexibilitySessions = Object.values(groupedData).filter(day => 
      day['balance/stretching'] > 0
    ).length;

    return {
      aerobic: {
        current: aerobicMinutes,
        target: EXERCISE_GOALS.aerobic.target,
        percentage: Math.min(100, (aerobicMinutes / EXERCISE_GOALS.aerobic.target) * 100)
      },
      strength: {
        current: strengthSessions,
        target: EXERCISE_GOALS.strength.target,
        percentage: Math.min(100, (strengthSessions / EXERCISE_GOALS.strength.target) * 100)
      },
      flexibility: {
        current: flexibilitySessions,
        target: EXERCISE_GOALS.flexibility.target,
        percentage: Math.min(100, (flexibilitySessions / EXERCISE_GOALS.flexibility.target) * 100)
      }
    };
  }, [weekData, groupedData]);

  // Calculate 3-month exercise progress
  const threeMonthProgress = useMemo(() => {
    if (!threeMonthData.length) return null;

    // Group 3-month data by day
    const threeMonthGroupedData = {};
    threeMonthData.forEach(item => {
      if (!item.date) return;
      const dayName = daysOfWeek[new Date(item.date).getDay()];
      if (!threeMonthGroupedData[dayName]) {
        threeMonthGroupedData[dayName] = { walking: 0, swimming: 0, running: 0, 'resistance/weights': 0, biking: 0, 'balance/stretching': 0, other: 0, totalMinutes: 0 };
      }
      threeMonthGroupedData[dayName].walking += Number(item.walking) || 0;
      threeMonthGroupedData[dayName].swimming += Number(item.swimming) || 0;
      threeMonthGroupedData[dayName].running += Number(item.running) || 0;
      threeMonthGroupedData[dayName]['resistance/weights'] += Number(item['resistance/weights']) || 0;
      threeMonthGroupedData[dayName].biking += Number(item.biking) || 0;
      threeMonthGroupedData[dayName]['balance/stretching'] += Number(item['balance/stretching']) || 0;
      threeMonthGroupedData[dayName].other += Number(item.other) || 0;
      threeMonthGroupedData[dayName].totalMinutes = threeMonthGroupedData[dayName].walking + threeMonthGroupedData[dayName].swimming + threeMonthGroupedData[dayName].running + threeMonthGroupedData[dayName]['resistance/weights'] + threeMonthGroupedData[dayName].biking + threeMonthGroupedData[dayName]['balance/stretching'] + threeMonthGroupedData[dayName].other;
    });

    // Calculate actual weeks in the 3-month period
    const { start: startOfThreeMonths, end: endOfThreeMonths } = nav.getThreeMonthRange();
    const daysInThreeMonths = Math.ceil((endOfThreeMonths - startOfThreeMonths) / (1000 * 60 * 60 * 24)) + 1;
    const weeksInThreeMonths = daysInThreeMonths / 7;

    // Calculate aerobic activity
    const aerobicMinutes = Object.values(threeMonthGroupedData).reduce((sum, day) => 
      sum + day.walking + day.swimming + day.running + day.biking, 0
    );
    const avgAerobicPerWeek = aerobicMinutes / weeksInThreeMonths;

    // Calculate strength training sessions
    const strengthSessions = Object.values(threeMonthGroupedData).filter(day => 
      day['resistance/weights'] >= 10
    ).length;
    const avgStrengthPerWeek = strengthSessions / weeksInThreeMonths;

    // Calculate flexibility/balance sessions
    const flexibilitySessions = Object.values(threeMonthGroupedData).filter(day => 
      day['balance/stretching'] > 0
    ).length;
    const avgFlexibilityPerWeek = flexibilitySessions / weeksInThreeMonths;

    return {
      aerobic: {
        current: avgAerobicPerWeek,
        target: EXERCISE_GOALS.aerobic.target,
        percentage: Math.min(100, (avgAerobicPerWeek / EXERCISE_GOALS.aerobic.target) * 100)
      },
      strength: {
        current: avgStrengthPerWeek,
        target: EXERCISE_GOALS.strength.target,
        percentage: Math.min(100, (avgStrengthPerWeek / EXERCISE_GOALS.strength.target) * 100)
      },
      flexibility: {
        current: avgFlexibilityPerWeek,
        target: EXERCISE_GOALS.flexibility.target,
        percentage: Math.min(100, (avgFlexibilityPerWeek / EXERCISE_GOALS.flexibility.target) * 100)
      }
    };
  }, [threeMonthData]);

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  // Render physician view with exercise progress bars
  if (viewMode === 'physician') {
    return (
      <div className={`exercise-chart-container ${isExpanded ? 'expanded' : ''}`}>
        <h3 className="chart-title">Exercise Goals Progress</h3>
        <h4 className="chart-subtitle">{nav.getCurrentMonthYear()}</h4>
        
        {exerciseProgress && (
          <div className="exercise-progress-container">
            <svg 
              width="100%" 
              viewBox={`0 0 ${config.width} ${config.height}`}
              className="exercise-progress-svg"
            >
              {/* Background bars */}
              <g className="progress-bars">
                {Object.entries(exerciseProgress).map(([key, progress], index) => {
                  const barWidth = 120;
                  const barHeight = 20;
                  const barSpacing = 40;
                  const startX = config.padding.left + 50;
                  const startY = config.padding.top + 50 + (index * barSpacing);
                  
                  const goal = EXERCISE_GOALS[key];
                  const percentage = Math.min(100, progress.percentage);
                  
                  return (
                    <g key={key} className="progress-bar-group">
                      {/* Background bar */}
                      <rect
                        x={startX}
                        y={startY}
                        width={barWidth}
                        height={barHeight}
                        fill="#e0e0e0"
                        rx="10"
                        ry="10"
                      />
                      {/* Progress bar */}
                      <rect
                        x={startX}
                        y={startY}
                        width={(percentage / 100) * barWidth}
                        height={barHeight}
                        fill={goal.color}
                        rx="10"
                        ry="10"
                      />
                      {/* Label */}
                      <text
                        x={startX - 10}
                        y={startY + barHeight / 2 + 5}
                        textAnchor="end"
                        fontSize="12"
                        fill="var(--chart-color-text-primary)"
                        className="progress-label"
                      >
                        {goal.label}
                      </text>
                      {/* Percentage text */}
                      <text
                        x={startX + barWidth + 10}
                        y={startY + barHeight / 2 + 5}
                        textAnchor="start"
                        fontSize="12"
                        fill="var(--chart-color-text-primary)"
                        className="progress-percentage"
                      >
                        {Math.round(percentage)}%
                      </text>
                      {/* Current/Target text */}
                      <text
                        x={startX}
                        y={startY + barHeight + 15}
                        textAnchor="start"
                        fontSize="10"
                        fill="var(--chart-color-text-secondary)"
                        className="progress-details"
                      >
                        {key === 'aerobic' ? `${Math.round(progress.current)}/${progress.target} min` :
                         key === 'strength' ? `${Math.round(progress.current)}/${progress.target} sessions` :
                         `${Math.round(progress.current)}/${progress.target} sessions`}
                      </text>
                    </g>
                  );
                })}
              </g>
            </svg>
          </div>
        )}

        {/* Show summary for physician view */}
        {exerciseProgress && (
          <div className="summary-container">
            <div className="chart-summary">
              <h4>Week Summary</h4>
              <div className="summary-stats">
                <div className="stat-item">
                  <span className="stat-label">Aerobic Activity:</span>
                  <span className="stat-value">
                    {Math.round(exerciseProgress.aerobic.current)}/{exerciseProgress.aerobic.target} minutes
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Strength Training:</span>
                  <span className="stat-value">
                    {Math.round(exerciseProgress.strength.current)}/{exerciseProgress.strength.target} sessions
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Flexibility/Balance:</span>
                  <span className="stat-value">
                    {Math.round(exerciseProgress.flexibility.current)}/{exerciseProgress.flexibility.target} sessions
                  </span>
                </div>
              </div>
            </div>
            
            {threeMonthProgress && (
              <div className="chart-summary">
                <h4>3-Month Average</h4>
                <div className="summary-stats">
                  <div className="stat-item">
                    <span className="stat-label">Aerobic Activity:</span>
                    <span className="stat-value">
                      {Math.round(threeMonthProgress.aerobic.current)}/{threeMonthProgress.aerobic.target} min/week
                    </span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Strength Training:</span>
                    <span className="stat-value">
                      {Math.round(threeMonthProgress.strength.current)}/{threeMonthProgress.strength.target} sessions/week
                    </span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Flexibility/Balance:</span>
                    <span className="stat-value">
                      {Math.round(threeMonthProgress.flexibility.current)}/{threeMonthProgress.flexibility.target} sessions/week
                    </span>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    );
  }

  // Original patient view with stacked bar chart
  const exerciseLegendItems = exerciseTypes.map(({ label, color, emoji }) => ({
    label: `${emoji} ${label}`,
    color,
  }));

  return (
      <div className={`exercise-chart-container ${isExpanded ? 'expanded' : ''}`}>
        <h3 className="chart-title">Activity Breakdown by Type</h3>
        <h4 className="chart-subtitle">{nav.getCurrentMonthYear()}</h4>
        
        <svg 
          width="100%" 
          viewBox={`0 0 ${config.width} ${config.height}`}
          className="exercise-svg"
        >
          <g className="y-axis">
            {yAxisLabels.map(value => {
              const y = config.padding.top + chartHeight - (value / maxDuration) * chartHeight;
              return (
                <g key={value} className="y-axis-grid-group">
                  <line className="chart-grid-line-horizontal" x1={config.padding.left} y1={y} x2={config.width - config.padding.right} y2={y} />
                  <text x={config.padding.left - 15} y={y + 3} fontSize={config.fontSize.yAxis} textAnchor="end" fill="var(--chart-color-text-secondary)" className="chart-tick-label">{value}</text>
                </g>
              );
            })}
            <text x={-(config.padding.top + chartHeight / 2)} y={config.padding.left / 2 - 5} fontSize={config.fontSize.yAxisTitle} textAnchor="middle" fill="var(--chart-color-text-primary)" transform="rotate(-90)" className="y-axis-title">Minutes</text>
          </g>

          <g className="x-axis">
            {daysOfWeek.map((day, dayIndex) => {
              const date = new Date(startOfWeek);
              date.setDate(date.getDate() + dayIndex);
              const x = config.padding.left + dayIndex * config.dayWidth + config.dayWidth / 2;

              const dayData = groupedData[day];
              const performedExercises = exerciseTypes.filter(type => (dayData[type.key] || 0) > 0);
              const emojis = performedExercises.map(type => type.emoji).join(' ');

              return (
                <g key={dayIndex} className="x-axis-label-group">
                  <line className="chart-grid-line-vertical" x1={config.padding.left + dayIndex * config.dayWidth} y1={config.padding.top} x2={config.padding.left + dayIndex * config.dayWidth} y2={config.height - config.padding.bottom} />
                  <text x={x} y={config.height - config.padding.bottom + 15} textAnchor="middle" fontSize={config.fontSize.dayLabel} fill="var(--chart-color-text-primary)" className="x-axis-day-label">{day}</text>
                  <text x={x} y={config.height - config.padding.bottom + 30} textAnchor="middle" fontSize={config.fontSize.dateLabel} fill="var(--chart-color-text-secondary)" className="x-axis-date-label">{date.getDate()}</text>
                  <text x={x} y={config.height - config.padding.bottom + 48} textAnchor="middle" fontSize={config.fontSize.emoji}>{emojis}</text>
                </g>
              );
            })}
          </g>

          <g className="data-bars">
            {daysOfWeek.map((day, dayIndex) => {
              const dayData = groupedData[day];
              const x = config.padding.left + dayIndex * config.dayWidth + config.dayWidth / 2;
              let currentY = config.height - config.padding.bottom;

              return (
                <g key={day}>
                  {exerciseTypes.map(type => {
                    const minutes = Number(dayData[type.key]) || 0;
                    if (minutes <= 0) return null;

                    const segmentHeight = (minutes / maxDuration) * chartHeight;
                    const segmentY = currentY - segmentHeight;

                    const segment = (
                      <g key={type.key}>
                        <rect
                          x={x - config.barWidth / 2}
                          y={segmentY}
                          width={config.barWidth}
                          height={segmentHeight}
                          fill={type.color}
                          className="data-bar"
                        >
                          <title>{`${type.label}: ${Math.round(minutes)} min`}</title>
                        </rect>
                        {/* Duration label inside the segment */}
                        {segmentHeight >= 12 && (
                          <text
                            x={x}
                            y={segmentY + segmentHeight / 2}
                            textAnchor="middle"
                            fontSize={isExpanded ? 8 : 6}
                            fill="black"
                            className="duration-label"
                            style={{ 
                              fontWeight: 'bold'
                            }}
                          >
                            {Math.round(minutes)}
                          </text>
                        )}
                      </g>
                    );

                    currentY = segmentY;
                    return segment;
                  })}
                </g>
              );
            })}
          </g>
        </svg>
        
        <Legend 
          title="Exercise Type" 
          items={exerciseLegendItems} 
          orientation="horizontal" 
          size="medium" 
        />
      </div>
  );
};

export default ExerciseChart;
