import { useState, useEffect, useCallback } from 'react';
import DataService from '../services/dataService';

/**
 * Custom hook for loading and managing patient data
 * @param {string} patientId - The patient identifier
 * @returns {Object} Object containing data, loading state, error state, and refetch function
 */
const usePatientData = (patientId) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      if (!patientId || typeof patientId !== 'string') {
        setLoading(false);
        setData(null);
        setError(null);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        const patientData = await DataService.getPatientData(patientId);

        if (patientData) {
          setData(patientData);
        } else {
          throw new Error('No data returned from service');
        }
      } catch (err) {
        const errorMessage = err instanceof Error ? err.message : 'Unknown error occurred';
        setError(errorMessage);
        setData(null);
        console.error('Error loading patient data:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [patientId]);

  const fetchData = useCallback(async () => {
    if (!patientId) return;

    try {
      setLoading(true);
      setError(null);

      const patientData = await DataService.getPatientData(patientId);
      setData(patientData);
    } catch (err) {
      setError(err.message);
      console.error('Error refetching patient data:', err);
    } finally {
      setLoading(false);
    }
  }, [patientId]);

  const refetch = useCallback(() => {
    fetchData();
  }, [fetchData]);

  return {
    data,
    loading,
    error,
    refetch,
    // Convenience accessors for specific data types
    patientInfo: data?.patientInfo || null,
    glucoseData: data?.glucoseData || [],
    bloodPressureData: data?.bloodPressureData || [],
    exerciseData: data?.exerciseData || [],
    moodData: data?.moodData || [],
    painData: data?.painData || [],
    mealData: data?.mealData || [],
    sleepData: data?.sleepData || []
  };
};

export default usePatientData;
