import { 
  SIZING_CONSTANTS, 
  calculateResponsiveDimensions, 
  getConsistentPadding, 
  getConsistentFontSizes 
} from '../../constants';

/**
 * Factory for creating chart configurations with consistent sizing
 * Uses unified sizing logic for all chart types
 */
export const createChartConfig = (type, isExpanded, containerWidth = 400) => {
  // Get consistent dimensions and styling
  const { width, height } = calculateResponsiveDimensions(containerWidth, isExpanded);
  const padding = getConsistentPadding(isExpanded);
  const fontSizes = getConsistentFontSizes(isExpanded);
  
  // Base configuration that all charts share
  const baseConfig = {
    width,
    height,
    padding,
    fontSize: fontSizes,
    expanded: isExpanded,
    containerWidth
  };

  switch (type) {
    case 'glucose':
      return {
        ...baseConfig,
        // Glucose-specific settings - More generous padding to prevent cutoff
        dayWidth: isExpanded ? 60 : 50, // Increased from 54/45
        yAxisLabels: [0, 4, 8, 12],
        yAxisRange: 12,
        barWidth: isExpanded ? 10 : 8, // Increased from 8/6
        // Custom padding for glucose chart
        padding: {
          top: isExpanded ? 80 : 70,    // Increased for y-axis title
          right: isExpanded ? 40 : 30,  // Increased for right margin
          bottom: isExpanded ? 80 : 60, // Increased for x-axis labels
          left: isExpanded ? 70 : 50    // Increased for y-axis labels
        }
      };

    case 'bloodPressure':
      return {
        ...baseConfig,
        // Blood pressure-specific settings - Better padding for readability
        yAxisRange: { systolic: 200, diastolic: 120 },
        yAxisOffset: { systolic: 0, diastolic: 0 },
        yAxisLabels: {
          systolic: isExpanded ? [0, 40, 80, 120, 160, 200] : [0, 100, 200],
          diastolic: isExpanded ? [0, 20, 40, 60, 80, 100, 120] : [0, 60, 120],
        },
        dayWidth: (width - padding.left - padding.right) / 7,
        barWidth: isExpanded ? 14 : 8, // Increased from 12/6
        dayPadding: isExpanded ? 10 : 6, // Increased from 8/4
        // Custom padding for blood pressure chart
        padding: {
          top: isExpanded ? 50 : 40,    // Increased for y-axis title
          right: isExpanded ? 30 : 20,  // Increased for right margin
          bottom: isExpanded ? 80 : 60, // Increased for x-axis labels
          left: isExpanded ? 80 : 60    // Increased for y-axis labels
        }
      };

    case 'exercise':
      return {
        ...baseConfig,
        // Exercise-specific settings - Better spacing and padding
        dayWidth: isExpanded ? 100 : 45, // Increased from 90/38
        barWidth: isExpanded ? 30 : 22,  // Increased from 25/20
        // Custom padding for exercise chart
        padding: {
          top: isExpanded ? 40 : 30,    // Increased for y-axis title
          right: isExpanded ? 20 : 10,  // Increased for right margin
          bottom: isExpanded ? 80 : 60, // Increased for x-axis labels
          left: isExpanded ? 60 : 50    // Increased for y-axis labels
        }
      };

    case 'mealContents':
      // Calculate meal-specific dimensions
      const mealHeight = isExpanded ? 90 : 70; // Increased from 80/60
      const totalMealHeight = 4 * mealHeight;
      const mealDayWidth = (width - padding.left - padding.right) / 7;
      
      return {
        ...baseConfig,
        height: totalMealHeight + padding.top + padding.bottom,
        // Meal-specific settings
        mealHeight,
        dayWidth: mealDayWidth,
        totalMealHeight,
        // Custom padding for meal chart
        padding: {
          top: isExpanded ? 50 : 40,    // Increased for y-axis labels
          right: isExpanded ? 40 : 30,  // Increased for right margin
          bottom: isExpanded ? 100 : 80, // Increased for x-axis labels
          left: isExpanded ? 150 : 130  // Increased for y-axis labels
        }
      };

    case 'sleep':
      return {
        ...baseConfig,
        // Sleep-specific settings
        dayWidth: (width - padding.left - padding.right) / 7,
        barWidth: isExpanded ? 14 : 8, // Increased from 12/6
        // Custom padding for sleep chart
        padding: {
          top: isExpanded ? 50 : 40,
          right: isExpanded ? 30 : 20,
          bottom: isExpanded ? 80 : 60,
          left: isExpanded ? 60 : 50
        }
      };

    case 'pain':
      return {
        ...baseConfig,
        // Pain-specific settings - Larger SVG and text
        bodySvgWidth: isExpanded ? 120 : 100, // Increased from 100/80
        bodySvgHeight: isExpanded ? 240 : 200, // Increased from 200/160
        // Custom padding for pain chart
        padding: {
          top: isExpanded ? 40 : 30,
          right: isExpanded ? 30 : 20,
          bottom: isExpanded ? 60 : 40,
          left: isExpanded ? 40 : 30
        }
      };

    case 'mood':
      return {
        ...baseConfig,
        // Mood-specific settings - Better spacing
        dayWidth: (width - padding.left - padding.right) / 7,
        cellSize: isExpanded ? 50 : 35, // Increased from 40/30
        // Custom padding for mood chart
        padding: {
          top: isExpanded ? 60 : 50,    // Increased for header
          right: isExpanded ? 40 : 30,  // Increased for right margin
          bottom: isExpanded ? 60 : 50, // Increased for bottom margin
          left: isExpanded ? 60 : 50    // Increased for left margin
        }
      };

    default:
      return baseConfig;
  }
};
