import React, { useState, useRef, useLayoutEffect } from 'react';
import usePatientData from '../../hooks/usePatientData';
import ChartContainer from '../ChartContainer';
import Legend from '../Legend';
import './BloodPressureChart.css';
import '../ChartStyles.css';

// --- Constants ---
const TIME_LABELS = ['12a', '12p'];
const TIME_HOURS = [0, 12];

const getChartConfig = (isExpanded) => {
  const config = {
    width: isExpanded ? 700 : 380,      // Fixed width for expanded and normal states
    height: isExpanded ? 250 : 180,     // Fixed height for each sub-chart (systolic/diastolic)
    padding: {
      top: 40,
      right: 20,
      bottom: 50,
      left: 60,
    },
    yAxisRange: { systolic: 200, diastolic: 120 },
    yAxisOffset: { systolic: 0, diastolic: 0 },
    fontSize: {
      yAxis: isExpanded ? 11 : 9,
      yAxisTitle: isExpanded ? 12 : 10,
      timeLabel: isExpanded ? 10 : 8,
      dayLabel: isExpanded ? 14 : 11,
      dateLabel: isExpanded ? 12 : 10,
    },
  };

  // Calculate dimensions based on the fixed configuration
  const contentWidth = config.width - config.padding.left - config.padding.right;
  config.dayWidth = contentWidth / 7;
  config.barWidth = isExpanded ? 8 : 6;
  config.dayPadding = isExpanded ? 6 : 4;

  // Y-axis labels change based on the expanded state for clarity.
  config.yAxisLabels = {
    systolic: isExpanded ? [0, 40, 80, 120, 160, 200] : [0, 100, 200],
    diastolic: isExpanded ? [0, 20, 40, 60, 80, 100, 120] : [0, 60, 120],
  };

  return config;
};

// --- Helper Functions ---
const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day;
  return new Date(d.setDate(diff));
};

const formatDayLabel = (date) => date.toLocaleDateString('en-US', { weekday: 'short' });

const getTimePosition = (date, dayIndex, config) => {
  const hour = date.getHours() + date.getMinutes() / 60;
  const timeRatio = hour / 24;
  const dayContentWidth = config.dayWidth - config.dayPadding * 2;
  return config.padding.left + (dayIndex * config.dayWidth) + config.dayPadding + (timeRatio * dayContentWidth);
};

// --- Chart Sub-components ---

const YAxis = ({ config, type }) => {
  const chartHeight = config.height - config.padding.top - config.padding.bottom;
  const yAxisLabels = config.yAxisLabels[type];
  const valueRange = config.yAxisRange[type];
  const yOffset = config.yAxisOffset[type];

  return (
    <g className="y-axis">
      <text
        x={config.padding.left / 3}
        y={config.padding.top + chartHeight / 2}
        fontSize={config.fontSize.yAxisTitle}
        textAnchor="middle"
        fill="#666"
        transform={`rotate(-90, ${config.padding.left / 3}, ${config.padding.top + chartHeight / 2})`}
      >
        mm Hg
      </text>
      {yAxisLabels.map(value => {
        const y = config.height - config.padding.bottom - ((value - yOffset) / valueRange) * chartHeight;
        return (
          <g key={value} className="y-axis-grid-group">
                        <line className="chart-grid-line-horizontal" x1={config.padding.left} y1={y} x2={config.width - config.padding.right} y2={y} />
            <text x={config.padding.left - 10} y={y + 3} fontSize={config.fontSize.yAxis} textAnchor="end" fill="#666">{value}</text>
          </g>
        );
      })}
    </g>
  );
};

const XAxis = ({ config, weekDays }) => (
  <g className="x-axis">
    {weekDays.map((day, dayIndex) => {
      const dayX = config.padding.left + dayIndex * config.dayWidth;
      const dayCenterX = dayX + config.dayWidth / 2;

      return (
        <g key={dayIndex} className="x-axis-label-group">
          {/* Time Labels */}
          {TIME_LABELS.map((timeLabel, timeIndex) => (
            <text
              key={timeIndex}
              className="time-label"
              x={dayX + (timeIndex === 0 ? 5 : config.dayWidth - 5)}
              y={config.height - config.padding.bottom + 15}
              textAnchor={timeIndex === 0 ? 'start' : 'end'}
              fontSize={config.fontSize.timeLabel}
              fill="#999"
            >
              {timeLabel}
            </text>
          ))}

          {/* Day and Date Labels */}
          <text
            x={dayCenterX}
            y={config.height - config.padding.bottom + 30}
            textAnchor="middle"
            className="day-label"
            fontSize={config.fontSize.dayLabel}
            fill="#666"
          >
            {formatDayLabel(day)}
          </text>
          <text
            x={dayCenterX}
            y={config.height - config.padding.bottom + 45}
            textAnchor="middle"
            className="date-label"
            fontSize={config.fontSize.dateLabel}
            fill="#888"
          >
            {day.getDate()}
          </text>
        </g>
      );
    })}
  </g>
);

const GridLines = ({ config, weekDays }) => (
  <g className="grid-lines">
    {/* Vertical day and time lines */}
    {weekDays.map((_, dayIndex) => {
      const dayX = config.padding.left + dayIndex * config.dayWidth;
      return (
        <g key={dayIndex}>
                    <line className="chart-grid-line-vertical" x1={dayX} y1={config.padding.top} x2={dayX} y2={config.height - config.padding.bottom} />
          {TIME_HOURS.map(hour => {
            const timeRatio = hour / 24;
            const x = config.padding.left + (dayIndex * config.dayWidth) + (timeRatio * config.dayWidth);
                        return <line key={hour} className="chart-grid-line-vertical-time" x1={x} y1={config.padding.top} x2={x} y2={config.height - config.padding.bottom} />;
          })}
        </g>
      );
    })}
  </g>
);

const DataBars = ({ readings, type, config }) => {
  const chartHeight = config.height - config.padding.top - config.padding.bottom;
  const valueRange = config.yAxisRange[type];
  const yOffset = config.yAxisOffset[type];
  const getColor = type === 'systolic' ? getSystolicColor : getDiastolicColor;

  return (
    <g className="data-bars">
      {readings.map((reading, index) => {
        const readingDate = new Date(reading.date);
        const dayIndex = readingDate.getDay();
        const x = getTimePosition(readingDate, dayIndex, config);
        const value = reading[type];
        const barHeight = (value / valueRange) * chartHeight;
        const y = config.height - config.padding.bottom - barHeight;

        return (
          <rect
            key={index}
            x={x - config.barWidth / 2}
            y={y}
            width={config.barWidth}
            height={Math.max(0, barHeight)}
            fill={getColor(value)}
          >
            <title>{`${value} mmHg - ${readingDate.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`}</title>
          </rect>
        );
      })}
    </g>
  );
};

const Chart = ({ type, weekData, isExpanded, weekDays }) => {
  const config = getChartConfig(isExpanded);
  const readings = weekData.filter(d => d[type] && d[type] > 0);

  return (
    <div className={`chart-section ${isExpanded ? 'expanded' : ''}`}>
      <h4 className="chart-subtitle">{type === 'systolic' ? 'Systolic' : 'Diastolic'}</h4>
      <div className="bp-svg-container">
        <svg
          width="100%"
          height="100%"
          viewBox={`0 0 ${config.width} ${config.height}`}
          className="chart-svg"
          preserveAspectRatio="xMidYMid meet"
        >
          <GridLines config={config} weekDays={weekDays} />
          <YAxis config={config} type={type} />
          <XAxis config={config} weekDays={weekDays} />
          <DataBars readings={readings} type={type} config={config} />
        </svg>
      </div>
    </div>
  );
};

const getSystolicColor = (value) => {
  if (value < 120) return '#4CAF50';
  if (value < 130) return '#FFC107';
  return '#F44336';
};

const getDiastolicColor = (value) => {
  if (value < 80) return '#4CAF50';
  if (value < 90) return '#FFC107';
  return '#F44336';
};

const bloodPressureLegendItems = [
  { color: '#4CAF50', label: 'Normal' },
  { color: '#FFC107', label: 'Elevated' },
  { color: '#F44336', label: 'High' },
];


// --- Main Component ---
const BloodPressureChart = ({ patientId, isExpanded = false, onExpand }) => {
  const { bloodPressureData, loading, error } = usePatientData(patientId, 'bloodPressure');
  const [currentWeek, setCurrentWeek] = useState(new Date(2025, 4, 1));

  const weekStart = getWeekStart(currentWeek);
  const weekEnd = new Date(weekStart);
  weekEnd.setDate(weekEnd.getDate() + 6);

  const weekData = bloodPressureData.filter(d => {
    const readingDate = new Date(d.date);
    return readingDate >= weekStart && readingDate <= weekEnd;
  });

  const weekDays = Array.from({ length: 7 }, (_, i) => {
    const d = new Date(weekStart);
    d.setDate(weekStart.getDate() + i);
    return d;
  });

  const goToPreviousWeek = () => setCurrentWeek(prev => new Date(prev.setDate(prev.getDate() - 7)));
  const goToNextWeek = () => setCurrentWeek(prev => new Date(prev.setDate(prev.getDate() + 7)));

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  return (
    <ChartContainer
      title="Blood Pressure"
      icon="❤️"
      isLoading={loading}
      error={error}
      noData={!loading && !error && (!bloodPressureData || bloodPressureData.length === 0)}
      onPrev={goToPreviousWeek}
      onNext={goToNextWeek}
      navigationLabel={formatDateRange(weekStart, weekEnd)}
    >
      <div className="bp-chart-container">
        <Legend title="Blood Pressure Category:" items={bloodPressureLegendItems} />
        <div className={`bp-chart-content ${isExpanded ? 'expanded' : ''}`}>
        <div className="bp-charts-wrapper">
          <Chart 
            type="systolic" 
            weekData={weekData} 
            isExpanded={isExpanded} 
            weekDays={weekDays} 
          />
          <Chart 
            type="diastolic" 
            weekData={weekData} 
            isExpanded={isExpanded} 
            weekDays={weekDays} 
          />
        </div>
      </div>
      </div>
    </ChartContainer>
  );
};

export default BloodPressureChart;
