// Application constants
export const PATIENT_COUNT = 100;

// Generate patient IDs array
export const PATIENTS = Array.from({ length: PATIENT_COUNT }, (_, i) => 
  `Patient_${(i + 1).toString().padStart(3, '0')}`
);

// Chart color scheme
export const CHART_COLORS = {
  glucose: '#1b9e77', // Updated to use new color scheme (green)
  bloodPressure: '#1f78b4', // Updated to use new color scheme (blue)
  exercise: '#1f78b4', // Updated to use new exercise color scheme (swimming blue)
  mood: '#d95f02', // Updated to use new color scheme (orange)
  pain: '#d73027', // Updated to use RdYlBu color for severe pain
  mealContents: '#27ae60',
  sleep: '#a6cee3' // Updated to use new color scheme (light blue)
};

// Visualization configurations - Updated with consistent naming and no emojis
export const VISUALIZATION_CONFIG = {
  glucose: { name: 'Blood Glucose', color: CHART_COLORS.glucose },
  bloodPressure: { name: 'Blood Pressure', color: CHART_COLORS.bloodPressure },
  exercise: { name: 'Exercise', color: CHART_COLORS.exercise },
  mood: { name: 'Mood', color: CHART_COLORS.mood },
  pain: { name: 'Pain', color: CHART_COLORS.pain },
  mealContents: { name: 'Meal Contents', color: CHART_COLORS.mealContents },
  sleep: { name: 'Sleep', color: CHART_COLORS.sleep }
};

// Shared dashboard configuration - now supports unlimited charts
export const DASHBOARD_CONFIG = {
  patient: {
    // No longer using fixed defaultVisualizations since we show all available
    defaultVisualizations: {},
    maxCharts: null // null means unlimited
  },
  physician: {
    // No longer using fixed defaultVisualizations since we show all available
    defaultVisualizations: {},
    maxCharts: null // null means unlimited
  }
};
