    else if (type.includes('biking')) dayData.biking += minutes;
    else if (type.includes('balance') || type.includes('stretching')) dayData['balance/stretching'] += minutes;
    else dayData.other += minutes;
    dayData.totalMinutes += minutes;
  }

  static processMoodData(rows) {
    return rows.filter(row => row['Mood']).map(row => ({
        date: new Date(row['Date']),
        mood: row['Mood'],
        category: row['Mood'] || 'neutral'
    }));
  }

  static processPainData(rows) {
    return rows.filter(row => row['Pain_Location'] && row['Pain_Level']).map(row => ({
        date: new Date(row['Date']),
        location: row['Pain_Location'].toLowerCase(),
        level: parseInt(row['Pain_Level'])
    }));
  }

  static processSleepData(rows) {
    return rows.filter(row => row['Sleep_Hours'] && row['Sleep_Quality']).map(row => ({
        date: new Date(row['Date']),
        hours: parseFloat(row['Sleep_Hours']),
        quality: row['Sleep_Quality'],
        qualityCode: parseInt(row['Sleep_Quality_Code'])
    }));
  }

  static processMealData(rows) {
    // Return the raw rows with meal data since MealContentsChart expects individual meal components
    return rows.filter(row => {
      // Check if any meal data exists for this row
      const mealTypes = ['Breakfast', 'Lunch', 'Dinner'];

      const hasMealData = mealTypes.some(mealType => {
        const time = row[`${mealType}_Time`];
        const protein = row[`${mealType}_Protein`];
        const carbs = row[`${mealType}_Carbohydrates`];
        const vegetables = row[`${mealType}_Vegetables`];
        const fruit = row[`${mealType}_Fruit`];
        const alcohol = row[`${mealType}_Alcohol`];
        const sugar = row[`${mealType}_Added_Sugar`];

        // Check if time exists and at least one food component is present
        return time && (protein === '1' || carbs === '1' || vegetables === '1' || fruit === '1' || alcohol === '1' || sugar);
      });

      return hasMealData;
    });
  }
}

export default DataService;
