import React from 'react';
import './InfoBox.css';

const InfoBox = ({ title, content, className = '' }) => {
  return (
    <div className={`info-box ${className}`}>
      {title && <h4 className="info-box-title">{title}</h4>}
      <div className="info-box-content">
        {content}
      </div>
    </div>
  );
};

export default InfoBox;
