/**
 * PatientMedications.js - Patient Medications Component
 * 
 * Displays patient current medications with dosage and schedule information.
 * Part of the split patient information layout.
 */

import React from 'react';
import './PatientInfoBox.css';

const PatientMedications = ({ 
  patientInfo, 
  loading = false, 
  error = null, 
  className = '' 
}) => {
  if (loading) {
    return (
      <div className={`patient-info-box medications ${className}`}>
        <h3>Current Medications</h3>
        <p>Loading medications...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`patient-info-box medications ${className}`}>
        <h3>Current Medications</h3>
        <p>Error loading data.</p>
      </div>
    );
  }

  if (!patientInfo) {
    return (
      <div className={`patient-info-box medications ${className}`}>
        <h3>Current Medications</h3>
        <p>No patient data found.</p>
      </div>
    );
  }

  return (
    <div className={`patient-info-box medications ${className}`}>
      <h3>Current Medications</h3>
      <div className="info-content">
        {patientInfo.detailedMedications?.length ? (
          <div className="medications-list">
            {patientInfo.detailedMedications.map((med, i) => (
              <div key={i} className="medication-item">
                <div className="medication-name">{med.name}</div>
                <div className="medication-details">
                  <span className="medication-schedule">{med.schedule}</span>
                  {med.dosage && <span className="medication-dosage"> - {med.dosage}</span>}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-data">No medications recorded</div>
        )}
      </div>
    </div>
  );
};

export default PatientMedications;
