import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useParams } from 'react-router-dom';
import WelcomeScreen from './WelcomeScreen';
import PatientDashboard from './PatientDashboard';
import PhysicianDashboard from './PhysicianDashboard';
import PatientSelector from './PatientSelector';
import GlucoseChart from './components/physician_charts/GlucoseChart';
import BloodPressureChart from './components/physician_charts/BloodPressureChart';
import ExerciseChart from './components/physician_charts/ExerciseChart';
import MealContentsChart from './components/physician_charts/MealContentsChart';
import ErrorBoundary from './components/ErrorBoundary';
import './App.css';

// Constants
const PATIENT_COUNT = 100;
const PATIENTS = Array.from({ length: PATIENT_COUNT }, (_, i) => `Patient_${(i+1).toString().padStart(3, '0')}`);

// Dummy patient data for demonstration
const PATIENT_DATA = {
  Patient_001: {
    name: 'John Smith',
    age: 45,
    diseases: ['Diabetes', 'Hypertension'],
    medications: {
      scheduled: ['Metformin', 'Lisinopril'],
      asNeeded: ['Ibuprofen']
    }
  },
  // ...add more patients as needed
};

// Default patient info when data is not available
const getDefaultPatientInfo = (patientId) => ({
  name: patientId,
  age: 'N/A',
  diseases: [],
  medications: { scheduled: [], asNeeded: [] }
});


function App() {
  return (
    <ErrorBoundary>
      <Router>
        <Routes>
          <Route path="/" element={<WelcomeScreen />} />
          <Route path="/patient" element={<PatientEntry />} />
          <Route path="/patient/:patientId" element={<PatientDashboardWrapper />} />
          <Route path="/physician" element={<PhysicianDashboardWrapper />} />
        </Routes>
      </Router>
    </ErrorBoundary>
  );
}

function PatientEntry() {
  const [selectedPatient, setSelectedPatient] = useState('');
  const navigate = useNavigate();

  const handleGoToDashboard = () => {
    if (selectedPatient) {
      navigate(`/patient/${selectedPatient}`);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 80 }}>
      <h2>Please select your patient ID #.</h2>
      <PatientSelector
        patients={PATIENTS}
        value={selectedPatient}
        onChange={setSelectedPatient}
      />
      <button
        style={{ fontSize: 20, marginTop: 24, padding: '10px 30px' }}
        onClick={handleGoToDashboard}
        disabled={!selectedPatient}
      >
        Go to Dashboard
      </button>
    </div>
  );
}

function PatientDashboardWrapper() {
  const { patientId } = useParams();
  const info = PATIENT_DATA[patientId] || getDefaultPatientInfo(patientId);

  return (
    <PatientDashboard
      patientId={patientId}
      patientInfo={{ name: info.name, age: info.age, diseases: info.diseases }}
      medicationInfo={info.medications}
    />
  );
}

function PhysicianDashboardWrapper() {
  return (
    <PhysicianDashboard>
      <GlucoseChart />
      <BloodPressureChart />
      <ExerciseChart />
      <MealContentsChart />
    </PhysicianDashboard>
  );
}

export default App;
