import React, { useState, useMemo } from 'react';
import usePatientData from '../../hooks/usePatientData';
import ChartContainer from '../ChartContainer';
import Legend from '../Legend';
import '../ChartStyles.css';
import './PhysicianPainChart.css';
import BodySVG from './BodySvg';

const PhysicianPainChart = ({ patientId, isExpanded = false, onExpand }) => {
  const { painData, isLoading: loading, error } = usePatientData(patientId, 'pain');
  const [currentWeek, setCurrentWeek] = useState(new Date(2025, 4, 1)); // Default to May 2025

  const handlePrevWeek = () => {
    setCurrentWeek(new Date(currentWeek.setDate(currentWeek.getDate() - 7)));
  };

  const handleNextWeek = () => {
    setCurrentWeek(new Date(currentWeek.setDate(currentWeek.getDate() + 7)));
  };

  const getPainColor = (level) => {
    if (level === 0) return '#E8F5E8'; // Very light green for no pain
    if (level <= 2) return '#FFE4B5'; // Light orange for mild pain
    if (level <= 4) return '#FFB347'; // Orange for moderate pain
    if (level <= 6) return '#FF8C69'; // Dark orange for moderate-severe pain
    if (level <= 8) return '#FF6347'; // Red for severe pain
    return '#DC143C'; // Dark red for extreme pain
  };

  const getPainIntensity = (level) => {
    if (level === 0) return 'No Pain';
    if (level <= 2) return 'Mild';
    if (level <= 4) return 'Moderate';
    if (level <= 6) return 'Moderate-Severe';
    if (level <= 8) return 'Severe';
    return 'Extreme';
  };

  const painLegendItems = Array.from({ length: 11 }, (_, i) => i).map(level => ({
    color: getPainColor(level),
    label: `${level}`,
  }));

  // Calculate week dates
  const startOfWeek = new Date(currentWeek);
  startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay());
  startOfWeek.setHours(0, 0, 0, 0);

  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(endOfWeek.getDate() + 6);
  endOfWeek.setHours(23, 59, 59, 999);

  const weekPainData = painData.filter(d => d.date >= startOfWeek && d.date <= endOfWeek);

  // Process data for chart
  const { chartData, mostCommonLocation, averagePainLevel } = useMemo(() => {
    if (!weekPainData.length) return { chartData: [], mostCommonLocation: null, averagePainLevel: 0 };

    // Create a map of dates to pain levels
    const dateMap = new Map();
    const locationCounts = new Map();

    weekPainData.forEach(item => {
      const dateKey = item.date.toDateString();
      dateMap.set(dateKey, item.level);
      
      // Count locations
      const location = item.location.toLowerCase();
      locationCounts.set(location, (locationCounts.get(location) || 0) + 1);
    });

    // Find most common location
    let mostCommon = null;
    let maxCount = 0;
    for (const [location, count] of locationCounts) {
      if (count > maxCount) {
        maxCount = count;
        mostCommon = location;
      }
    }

    // Generate chart data for all 7 days of the week
    const data = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(date.getDate() + i);
      const dateKey = date.toDateString();
      const painLevel = dateMap.get(dateKey) || 0;
      
      data.push({
        date,
        day: date.toLocaleDateString('en-US', { weekday: 'short' }),
        level: painLevel,
        color: getPainColor(painLevel)
      });
    }

    const totalPain = weekPainData.reduce((sum, item) => sum + item.level, 0);
    const avgPain = weekPainData.length > 0 ? (totalPain / weekPainData.length) : 0;

    return { 
      chartData: data, 
      mostCommonLocation: mostCommon, 
      averagePainLevel: avgPain.toFixed(1) 
    };
  }, [weekPainData, startOfWeek]);

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  // SVG Line Chart Component
  const LineChart = ({ data }) => {
    const width = 400;
    const height = 200;
    const padding = { top: 20, right: 20, bottom: 40, left: 40 };
    const chartWidth = width - padding.left - padding.right;
    const chartHeight = height - padding.top - padding.bottom;

    if (!data.length) return null;

    // Create path for line
    const pathData = data.map((point, index) => {
      const x = (index / (data.length - 1)) * chartWidth;
      const y = chartHeight - (point.level / 10) * chartHeight;
      return `${index === 0 ? 'M' : 'L'} ${x + padding.left} ${y + padding.top}`;
    }).join(' ');

    return (
      <div className="line-chart-container">
        <h4>Pain Levels Over Week</h4>
        <svg width={width} height={height} className="pain-line-chart">
          {/* Grid lines */}
          {[0, 2, 4, 6, 8, 10].map(level => (
            <g key={level}>
              <line
                x1={padding.left}
                y1={padding.top + chartHeight - (level / 10) * chartHeight}
                x2={padding.left + chartWidth}
                y2={padding.top + chartHeight - (level / 10) * chartHeight}
                stroke="#e0e0e0"
                strokeWidth="1"
              />
              <text
                x={padding.left - 5}
                y={padding.top + chartHeight - (level / 10) * chartHeight + 3}
                textAnchor="end"
                fontSize="10"
                fill="#666"
              >
                {level}
              </text>
            </g>
          ))}

          {/* X-axis labels */}
          {data.map((point, index) => (
            <text
              key={index}
              x={padding.left + (index / (data.length - 1)) * chartWidth}
              y={height - 10}
              textAnchor="middle"
              fontSize="10"
              fill="#666"
            >
              {point.day}
            </text>
          ))}

          {/* Line */}
          <path
            d={pathData}
            fill="none"
            stroke="#4A90E2"
            strokeWidth="2"
          />

          {/* Data points */}
          {data.map((point, index) => {
            const x = (index / (data.length - 1)) * chartWidth + padding.left;
            const y = chartHeight - (point.level / 10) * chartHeight + padding.top;
            return (
              <g key={index}>
                <circle
                  cx={x}
                  cy={y}
                  r="4"
                  fill={point.color}
                  stroke="#fff"
                  strokeWidth="2"
                />
                {point.level > 0 && (
                  <text
                    x={x}
                    y={y - 8}
                    textAnchor="middle"
                    fontSize="10"
                    fill="#333"
                    fontWeight="bold"
                  >
                    {point.level}
                  </text>
                )}
              </g>
            );
          })}

          {/* Axes */}
          <line
            x1={padding.left}
            y1={padding.top}
            x2={padding.left}
            y2={padding.top + chartHeight}
            stroke="#333"
            strokeWidth="1"
          />
          <line
            x1={padding.left}
            y1={padding.top + chartHeight}
            x2={padding.left + chartWidth}
            y2={padding.top + chartHeight}
            stroke="#333"
            strokeWidth="1"
          />

          {/* Y-axis label */}
          <text
            x={15}
            y={padding.top + chartHeight / 2}
            textAnchor="middle"
            fontSize="11"
            fill="#333"
            transform={`rotate(-90, 15, ${padding.top + chartHeight / 2})`}
          >
            Pain Level
          </text>
        </svg>
      </div>
    );
  };

  return (
    <ChartContainer
      title="Pain Analysis (Physician View)"
      icon="ðŸ©º"
      isLoading={loading}
      error={error}
      noData={!loading && !error && (!painData || painData.length === 0)}
      isExpanded={isExpanded}
      onExpand={onExpand}
      onPrev={handlePrevWeek}
      onNext={handleNextWeek}
      navigationLabel={formatDateRange(startOfWeek, endOfWeek)}
    >
      <div className="physician-pain-chart-container">
        <div className="pain-legend-wrapper">
          <Legend title="Pain Intensity Scale (0-10)" items={painLegendItems} />
        </div>
        
        <div className="pain-analysis-content">
          {/* Body SVG and Summary */}
          <div className="body-summary-section">
            <div className="body-display">
              <h4>Most Common Pain Location</h4>
              {mostCommonLocation ? (
                <BodySVG 
                  className="physician-body-svg" 
                  painLocation={mostCommonLocation} 
                  painColor={getPainColor(parseInt(averagePainLevel))} 
                />
              ) : (
                <div className="no-pain-display">
                  <BodySVG className="physician-body-svg" painLocation="" painColor="#E8F5E8" />
                  <p>No pain reported this week</p>
                </div>
              )}
            </div>
            
            <div className="pain-summary">
              <h4>Week Summary</h4>
              <div className="summary-stats">
                <div className="stat-item">
                  <span className="stat-label">Average Pain Level:</span>
                  <span className="stat-value" style={{ color: getPainColor(parseFloat(averagePainLevel)) }}>
                    {averagePainLevel}/10
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Most Common Location:</span>
                  <span className="stat-value">
                    {mostCommonLocation ? mostCommonLocation.charAt(0).toUpperCase() + mostCommonLocation.slice(1) : 'None'}
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Pain Intensity:</span>
                  <span className="stat-value">
                    {getPainIntensity(parseFloat(averagePainLevel))}
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Days with Pain:</span>
                  <span className="stat-value">
                    {chartData.filter(d => d.level > 0).length}/7
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Line Chart */}
          <div className="chart-section">
            <LineChart data={chartData} />
          </div>
        </div>
      </div>
    </ChartContainer>
  );
};

export default PhysicianPainChart;
