import React from 'react';

/**
 * Shared component for visualization containers used in both Patient and Physician dashboards
 * Replaces the duplicate VisualizationBox and VisualizationWindow components
 */
const VisualizationWrapper = ({ 
  id, 
  className, 
  selectedViz, 
  availableVisualizations, 
  allVisualizations,
  onVisualizationChange, 
  onExpand, 
  isExpanded, 
  renderVisualization,
  disabled = false,
  placeholderText = "No data available for this patient."
}) => {
  const viz = allVisualizations[selectedViz];
  
  return (
    <div className={className}>
      <div className="visualization-header">
        <select 
          value={selectedViz}
          onChange={(e) => onVisualizationChange(id, e.target.value)}
          style={{ color: viz?.color || '#2c3e50' }}
          disabled={disabled || Object.keys(availableVisualizations).length === 0}
        >
          {Object.entries(availableVisualizations).map(([key, viz]) => (
            <option key={key} value={key}>{viz.name}</option>
          ))}
        </select>
        <button onClick={() => onExpand(id)} className="expand-button">
          {isExpanded ? 'Collapse' : 'Expand'}
        </button>
      </div>
      <div className="visualization-content">
        {Object.keys(availableVisualizations).length === 0 ? (
          <div className="chart-placeholder">{placeholderText}</div>
        ) : (
          renderVisualization(selectedViz, id)
        )}
      </div>
    </div>
  );
};

export default VisualizationWrapper;
