import React, { useState, useEffect } from 'react';
import './Tooltip.css';

const Tooltip = ({ 
  isVisible, 
  content, 
  position = { x: 0, y: 0 }, 
  containerRef = null 
}) => {
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (isVisible && containerRef && containerRef.current) {
      const container = containerRef.current;
      const containerRect = container.getBoundingClientRect();
      
      // Calculate position relative to the container
      let x = position.x - containerRect.left;
      let y = position.y - containerRect.top;
      
      // Adjust position to keep tooltip within viewport
      const tooltipWidth = 200; // Approximate tooltip width
      const tooltipHeight = 80; // Approximate tooltip height
      
      // Position tooltip closer to the bar - slightly above and to the right
      x = x + 15; // Small offset to the right
      y = y - tooltipHeight - 5; // Position above the bar with small gap
      
      // Adjust horizontal position if tooltip would go outside container
      if (x + tooltipWidth > containerRect.width) {
        x = x - tooltipWidth - 30; // Move to the left of the cursor
      }
      
      // Adjust vertical position if tooltip would go outside container
      if (y < 0) {
        y = y + tooltipHeight + 20; // Move below the cursor
      }
      
      setTooltipPosition({ x, y });
    }
  }, [isVisible, position, containerRef]);

  if (!isVisible) return null;

  return (
    <div 
      className="custom-tooltip"
      style={{
        position: 'absolute',
        left: `${tooltipPosition.x}px`,
        top: `${tooltipPosition.y}px`,
        zIndex: 1000,
      }}
    >
      {content}
    </div>
  );
};

export default Tooltip;
