import React, { useState } from 'react';
import PatientSelector from './PatientSelector';
import usePatientData from './hooks/usePatientData';

import GlucoseChart from './components/patient_charts/GlucoseChart';
import BloodPressureChart from './components/patient_charts/BloodPressureChart';
import ExerciseChart from './components/patient_charts/ExerciseChart';
import MoodCalendar from './components/patient_charts/MoodCalendar';
import PhysicianPainChart from './components/patient_charts/PhysicianPainChart';
import MealContentsChart from './components/patient_charts/MealContentsChart';
import SleepChart from './components/patient_charts/SleepChart';

import './PhysicianDashboard.css';

const PATIENT_COUNT = 100;
const PATIENTS = Array.from({ length: PATIENT_COUNT }, (_, i) => `Patient_${(i + 1).toString().padStart(3, '0')}`);

const PhysicianDashboard = () => {
  const [selectedPatientId, setSelectedPatientId] = useState('');
  const { data, loading, error } = usePatientData(selectedPatientId);

  const allVisualizations = {
    glucose: { name: 'Blood Glucose', component: GlucoseChart, color: '#3498db' },
    bloodPressure: { name: 'Blood Pressure', component: BloodPressureChart, color: '#e74c3c' },
    exercise: { name: 'Exercise Chart', component: ExerciseChart, color: '#f39c12' },
    mood: { name: 'Mood Calendar', component: MoodCalendar, color: '#9b59b6' },
    pain: { name: 'Pain Analysis', component: PhysicianPainChart, color: '#e67e22' },
    mealContents: { name: 'Meal Contents', component: MealContentsChart, color: '#27ae60' },
    sleep: { name: 'Sleep Chart', component: SleepChart, color: '#34495e' },
  };

  // Check for available visualizations based on data
  const availableVisualizations = React.useMemo(() => {
    if (!data) return {};
    return Object.entries(allVisualizations).reduce((acc, [key, viz]) => {
      const dataKey = `${key}Data`;
      // Special case for meal contents
      if (key === 'mealContents' && data['mealData'] && data['mealData'].length > 0) {
        acc[key] = viz;
      } else if (data[dataKey] && data[dataKey].length > 0) {
        acc[key] = viz;
      }
      return acc;
    }, {});
  }, [data]);

  // Default selections for 6 windows
  const [selectedVisualizations, setSelectedVisualizations] = useState({
    window1: 'glucose',
    window2: 'bloodPressure',
    window3: 'exercise',
    window4: 'sleep',
    window5: 'mood',
    window6: 'pain'
  });

  const handleVisualizationChange = (windowId, visualizationType) => {
    setSelectedVisualizations(prev => ({ ...prev, [windowId]: visualizationType }));
  };

  const renderVisualization = (visualizationType, windowId) => {
    if (!selectedPatientId) {
      return <div className="chart-placeholder">Please select a patient to view data.</div>;
    }

    const viz = allVisualizations[visualizationType];
    if (!viz) return <div className="chart-placeholder">Invalid visualization</div>;

    const Component = viz.component;
    return <Component patientId={selectedPatientId} isPhysicianView={true} />;
  };

  // Component for individual visualization windows
  const VisualizationWindow = ({ windowId, className }) => {
    const selectedViz = selectedVisualizations[windowId];
    const viz = allVisualizations[selectedViz];

    return (
      <div className={`physician-chart-container ${className}`}>
        <div className="visualization-header">
          <select 
            value={selectedViz}
            onChange={(e) => handleVisualizationChange(windowId, e.target.value)}
            style={{ color: viz?.color || '#2c3e50' }}
            disabled={Object.keys(availableVisualizations).length === 0}
          >
            {Object.entries(availableVisualizations).map(([key, viz]) => (
              <option key={key} value={key}>{viz.name}</option>
            ))}
          </select>
        </div>
        <div className="visualization-content">
          {Object.keys(availableVisualizations).length === 0 ? (
            <div className="chart-placeholder">No data available for this patient.</div>
          ) : (
            renderVisualization(selectedViz, windowId)
          )}
        </div>
      </div>
    );
  };

  const patientInfo = data?.patientInfo;

  return (
    <div className="physician-dashboard">
      <div className="physician-header">
        <h1>Physician Dashboard</h1>
        <PatientSelector
          patients={PATIENTS}
          value={selectedPatientId}
          onChange={setSelectedPatientId}
        />
      </div>

      {selectedPatientId && (
        <div className="dashboard-content">
          <div className="patient-info-card-physician">
            <h3>Patient Information</h3>
            {loading && <p>Loading patient info...</p>}
            {error && <p>Error loading data.</p>}
            {patientInfo && (
              <>
                <div><span>Patient #:</span> {patientInfo.patientNumber}</div>
                <div><span>Age:</span> {patientInfo.age} years</div>
                <div><span>Gender:</span> {patientInfo.gender || 'Not specified'}</div>
                <div className="conditions-section">
                  <span>Conditions:</span>
                  <div className="conditions-list">
                    {patientInfo.conditions?.map((condition, i) => (
                      <div key={i} className="condition-item">{condition}</div>
                    ))}
                  </div>
                </div>
                <div className="medications-section">
                  <span>Medications:</span>
                  <div className="medications-list">
                    {patientInfo.detailedMedications?.map((med, i) => (
                      <div key={i} className="medication-item">{med.name}</div>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>

          <div className="physician-charts-grid">
            <VisualizationWindow windowId="window1" className="window-1" />
            <VisualizationWindow windowId="window2" className="window-2" />
            <VisualizationWindow windowId="window3" className="window-3" />
            <VisualizationWindow windowId="window4" className="window-4" />
            <VisualizationWindow windowId="window5" className="window-5" />
            <VisualizationWindow windowId="window6" className="window-6" />
          </div>
        </div>
      )}
    </div>
  );
};

export default PhysicianDashboard;
