import React from 'react';
import PropTypes from 'prop-types';
import './Card.css';

/**
 * Reusable Card component for consistent styling across the app
 * 
 * @param {Object} props
 * @param {React.ReactNode} props.children - Content to display inside the card
 * @param {string} props.title - Optional title for the card header
 * @param {string} props.className - Additional CSS classes
 * @param {React.ReactNode} props.headerActions - Optional actions/buttons for the header
 */
function Card({ 
  children, 
  title, 
  className = '', 
  headerActions = null 
}) {
  return (
    <div className={`card ${className}`}>
      {title && (
        <div className="card-header">
          <h3 className="card-title">{title}</h3>
          {headerActions && (
            <div className="card-actions">
              {headerActions}
            </div>
          )}
        </div>
      )}
      <div className="card-content">
        {children}
      </div>
    </div>
  );
}

// PropTypes for better documentation and debugging
Card.propTypes = {
  children: PropTypes.node.isRequired,
  title: PropTypes.string,
  className: PropTypes.string,
  headerActions: PropTypes.node
};

export default Card;
