import React, { useState, useMemo, useEffect } from 'react';
import usePatientData from '../../hooks/usePatientData';
import ChartContainer from '../ChartContainer';
import Legend from '../Legend';
import './MealContentsChart.css';
import '../ChartStyles.css';

// --- Constants ---
const DAYS_OF_WEEK = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
const MEAL_TYPES = ['Breakfast', 'Lunch', 'Dinner'];
const FOOD_CATEGORIES = ['Protein', 'Carbohydrates', 'Vegetables', 'Fruit', 'Alcohol'];
const SUGAR_CATEGORIES = ['', '1-20g', '20-40g', '40-60g', '60g+'];
const DEFAULT_WEEK = new Date(2025, 4, 1);

// Emoji mappings for food categories
const CATEGORY_EMOJIS = {
  'Protein': 'ðŸ¥©',
  'Carbohydrates': 'ðŸž',
  'Vegetables': 'ðŸ¥¦',
  'Fruit': 'ðŸŽ',
  'Alcohol': 'ðŸ·'
};

const SUGAR_EMOJIS = {
  '': '',              // No sugar
  '1-20g': 'ðŸ­',       // Lollipop
  '20-40g': 'ðŸ«',      // Chocolate bar
  '40-60g': 'ðŸ©',      // Donut
  '60g+': 'ðŸŽ‚'         // Cake
};

const getChartConfig = (isExpanded, containerWidth = 500) => {
  // Calculate responsive dimensions based on container width
  const maxWidth = Math.min(containerWidth - 40, isExpanded ? 700 : 500); // Account for padding
  const maxHeight = isExpanded ? 400 : 300;

  // Calculate day width based on available space
  const availableWidth = maxWidth - 140; // Account for left padding (110) + right padding (30)

  // Ensure minimum viable width for the chart
  if (availableWidth < 420) { // 7 days * 60px minimum
    // For very small containers, reduce padding to fit content
    const adjustedPadding = Math.max(80, availableWidth * 0.2); // At least 80px padding
    const adjustedAvailableWidth = availableWidth - (140 - adjustedPadding);
    const dayWidth = Math.max(50, adjustedAvailableWidth / 7);
    const totalWidth = adjustedPadding + (dayWidth * 7);

    return {
      width: totalWidth,
      height: maxHeight,
      padding: { top: 40, right: 20, bottom: 80, left: adjustedPadding },
      dayWidth: dayWidth,
      mealHeight: isExpanded ? 80 : 60,
      fontSize: {
        dayLabel: isExpanded ? 10 : 8,
        dateLabel: isExpanded ? 8 : 6,
        mealLabel: isExpanded ? 8 : 6,
        timeLabel: isExpanded ? 6 : 5,
      },
    };
  }

  const dayWidth = Math.max(60, Math.min(85, availableWidth / 7)); // Min 60px to accommodate emojis and circles

  // Recalculate total width based on actual day width
  const totalWidth = 140 + (dayWidth * 7);

  return {
    width: totalWidth,
    height: maxHeight,
    padding: { top: 40, right: 30, bottom: 80, left: 110 },
    dayWidth: dayWidth,
    mealHeight: isExpanded ? 80 : 60,
    fontSize: {
      dayLabel: isExpanded ? 10 : 8,
      dateLabel: isExpanded ? 8 : 6,
      mealLabel: isExpanded ? 8 : 6,
      timeLabel: isExpanded ? 6 : 5,
    },
  };
};

// --- Helper Functions ---
const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day;
  return new Date(d.setDate(diff));
};

const formatDayLabel = (date) => date.toLocaleDateString('en-US', { weekday: 'short' });
const formatDateLabel = (date) => date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });

const getMealTime = (data, mealType) => {
  const timeKey = `${mealType}_Time`;
  return data[timeKey] || '';
};

const getMealData = (data, mealType) => {
  const mealData = {};
  FOOD_CATEGORIES.forEach(category => {
    const key = `${mealType}_${category}`;
    mealData[category] = data[key] === 1 || data[key] === '1';
  });

  const sugarKey = `${mealType}_Added_Sugar`;
  mealData['Added Sugar'] = data[sugarKey] || '';

  return mealData;
};

// --- Chart Sub-components ---

const XAxis = ({ config, weekDays }) => (
  <g className="x-axis">
    {weekDays.map((day, dayIndex) => {
      const x = config.padding.left + dayIndex * config.dayWidth + config.dayWidth / 2;
      return (
        <g key={dayIndex}>
          <text
            x={x}
            y={config.height - config.padding.bottom + 30}
            fontSize={config.fontSize.dayLabel}
            textAnchor="middle"
            fill="#333"
            fontWeight="600"
          >
            {DAYS_OF_WEEK[day.getDay()]}
          </text>
          <text
            x={x}
            y={config.height - config.padding.bottom + 45}
            fontSize={config.fontSize.dateLabel}
            textAnchor="middle"
            fill="#666"
          >
            {day.getDate()}
          </text>
          <line
            className="chart-grid-line-vertical"
            x1={config.padding.left + dayIndex * config.dayWidth}
            y1={config.padding.top}
            x2={config.padding.left + dayIndex * config.dayWidth}
            y2={config.height - config.padding.bottom}
          />
        </g>
      );
    })}
    <line
      className="chart-grid-line-vertical"
      x1={config.padding.left + 7 * config.dayWidth}
      y1={config.padding.top}
      x2={config.padding.left + 7 * config.dayWidth}
      y2={config.height - config.padding.bottom}
    />
  </g>
);

const YAxis = ({ config }) => {
  const mealTimes = {
    'Breakfast': '6-10am',
    'Lunch': '11-2pm', 
    'Dinner': '5-9pm'
  };

  return (
    <g className="y-axis">
      {MEAL_TYPES.map((meal, mealIndex) => {
        const y = config.padding.top + mealIndex * config.mealHeight + config.mealHeight / 2;
        return (
          <g key={meal}>
            <text
              x={config.padding.left - 25}
              y={y - 5}
              fontSize={config.fontSize.mealLabel}
              textAnchor="end"
              fill="#333"
              fontWeight="600"
            >
              {meal}
            </text>
            <text
              x={config.padding.left - 25}
              y={y + 10}
              fontSize={config.fontSize.timeLabel}
              textAnchor="end"
              fill="#666"
            >
              {mealTimes[meal]}
            </text>
            <line
              className="chart-grid-line-horizontal"
              x1={config.padding.left}
              y1={config.padding.top + mealIndex * config.mealHeight}
              x2={config.padding.left + 7 * config.dayWidth}
              y2={config.padding.top + mealIndex * config.mealHeight}
            />
          </g>
        );
      })}
      <line
        className="chart-grid-line-horizontal"
        x1={config.padding.left}
        y1={config.padding.top + 3 * config.mealHeight}
        x2={config.padding.left + 7 * config.dayWidth}
        y2={config.padding.top + 3 * config.mealHeight}
      />
    </g>
  );
};

const MealCell = ({ config, dayIndex, mealIndex, mealData, mealTime }) => {
  const x = config.padding.left + dayIndex * config.dayWidth + 5;
  const y = config.padding.top + mealIndex * config.mealHeight + 5;
  const cellWidth = config.dayWidth - 10;
  const cellHeight = config.mealHeight - 10;

  // Calculate grid layout for food categories (2x3 grid)
  const categoriesPerRow = 3;
  const categoryWidth = cellWidth / categoriesPerRow;
  const categoryHeight = (cellHeight - 20) / 2; // Reserve space for time and sugar

  // Calculate responsive circle radius and emoji size based on cell dimensions
  const minDimension = Math.min(categoryWidth, categoryHeight);
  const circleRadius = Math.max(6, Math.min(12, minDimension * 0.3)); // Min 6px, max 12px, 30% of min dimension
  const emojiFontSize = Math.max(6, Math.min(10, minDimension * 0.25)); // Min 6px, max 10px, 25% of min dimension

  return (
    <g className="meal-cell">
      {/* Background */}
      <rect
        x={x}
        y={y}
        width={cellWidth}
        height={cellHeight}
        fill="#f9f9f9"
        stroke="#ddd"
        strokeWidth="1"
      />

      {/* Meal time */}
      <text
        x={x + cellWidth / 2}
        y={y + 12}
        fontSize={config.fontSize.timeLabel}
        textAnchor="middle"
        fill="#666"
      >
        {mealTime}
      </text>

      {/* Food category emojis and sugar emoji in 6-circle grid */}
      {FOOD_CATEGORIES.map((category, index) => {
        const row = Math.floor(index / categoriesPerRow);
        const col = index % categoriesPerRow;
        const categoryX = x + col * categoryWidth + categoryWidth / 2;
        const categoryY = y + 20 + row * categoryHeight + categoryHeight / 2;
        const hasCategory = mealData[category];

        return (
          <g key={category}>
            {/* Background circle for visibility */}
            <circle
              cx={categoryX}
              cy={categoryY}
              r={circleRadius}
              fill={hasCategory ? "#f0f8ff" : "#f5f5f5"}
              stroke="#ddd"
              strokeWidth="1"
              opacity={hasCategory ? 1 : 0.3}
            />
            {/* Emoji */}
            {hasCategory && (
              <text
                x={categoryX}
                y={categoryY}
                fontSize={emojiFontSize}
                textAnchor="middle"
                dominantBaseline="central"
                style={{ fontFamily: 'Apple Color Emoji, Segoe UI Emoji, Noto Color Emoji, sans-serif' }}
              >
                {CATEGORY_EMOJIS[category]}
              </text>
            )}
          </g>
        );
      })}

      {/* Sugar emoji in 6th position (next to alcohol) */}
      {(() => {
        const sugarRow = 1; // Second row
        const sugarCol = 2; // Third column (next to alcohol which is at col 1 in row 1)
        const sugarX = x + sugarCol * categoryWidth + categoryWidth / 2;
        const sugarY = y + 20 + sugarRow * categoryHeight + categoryHeight / 2;
        const hasSugar = mealData['Added Sugar'] && SUGAR_EMOJIS[mealData['Added Sugar']];

        return (
          <g key="sugar">
            {/* Background circle for sugar emoji */}
            <circle
              cx={sugarX}
              cy={sugarY}
              r={circleRadius}
              fill={hasSugar ? "#fff0f5" : "#f5f5f5"}
              stroke="#ddd"
              strokeWidth="1"
              opacity={hasSugar ? 1 : 0.3}
            />
            {/* Sugar emoji */}
            {hasSugar && (
              <text
                x={sugarX}
                y={sugarY}
                fontSize={emojiFontSize}
                textAnchor="middle"
                dominantBaseline="central"
                style={{ fontFamily: 'Apple Color Emoji, Segoe UI Emoji, Noto Color Emoji, sans-serif' }}
              >
                {SUGAR_EMOJIS[mealData['Added Sugar']]}
              </text>
            )}
          </g>
        );
      })()}
    </g>
  );
};

const MealGrid = ({ config, weekDays, patientData }) => {
  if (!patientData || patientData.length === 0) {
    return null;
  }

  return (
    <g className="meal-grid">
      {weekDays.map((day, dayIndex) => {
        const dayData = patientData.find(d => {
          const dataDate = new Date(d.Date);
          return dataDate.toDateString() === day.toDateString();
        });

        if (!dayData) return null;

        return MEAL_TYPES.map((meal, mealIndex) => {
          const mealData = getMealData(dayData, meal);
          const mealTime = getMealTime(dayData, meal);

          return (
            <MealCell
              key={`${dayIndex}-${mealIndex}`}
              config={config}
              dayIndex={dayIndex}
              mealIndex={mealIndex}
              mealData={mealData}
              mealTime={mealTime}
            />
          );
        });
      })}
    </g>
  );
};


// --- Main Component ---
const MealContentsChart = ({ patientId = 'Patient_001', isExpanded, onExpand, isPhysicianView = false }) => {
  const [selectedWeek, setSelectedWeek] = useState(DEFAULT_WEEK);
  const [containerWidth, setContainerWidth] = useState(500);
  const { mealData: patientData, loading, error } = usePatientData(patientId);
  const containerRef = React.useRef(null);

  const config = getChartConfig(isExpanded, containerWidth);

  // Measure container width for responsive sizing
  useEffect(() => {
    const updateContainerWidth = () => {
      if (containerRef.current) {
        const width = containerRef.current.offsetWidth;
        setContainerWidth(width);
      }
    };

    updateContainerWidth();
    window.addEventListener('resize', updateContainerWidth);
    return () => window.removeEventListener('resize', updateContainerWidth);
  }, [isExpanded]);

  const weekStart = getWeekStart(selectedWeek);
  const weekDays = Array.from({ length: 7 }, (_, i) => {
    const day = new Date(weekStart);
    day.setDate(weekStart.getDate() + i);
    return day;
  });

  const handlePrevWeek = () => setSelectedWeek(prev => new Date(new Date(prev).setDate(prev.getDate() - 7)));
  const handleNextWeek = () => setSelectedWeek(prev => new Date(new Date(prev).setDate(prev.getDate() + 7)));

  const startOfWeek = new Date(weekStart);
  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(endOfWeek.getDate() + 6);

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  // Calculate summary statistics for physician view
  const weekSummary = useMemo(() => {
    if (!patientData || patientData.length === 0) return null;

    const weekData = patientData.filter(d => {
      const dataDate = new Date(d.Date);
      return dataDate >= startOfWeek && dataDate <= endOfWeek;
    });

    if (weekData.length === 0) return null;

    // Count food categories across all meals
    const categoryStats = {};
    FOOD_CATEGORIES.forEach(category => {
      categoryStats[category] = 0;
    });

    // Count sugar levels
    const sugarStats = {};
    SUGAR_CATEGORIES.filter(s => s !== '').forEach(sugar => {
      sugarStats[sugar] = 0;
    });

    let totalMealsLogged = 0;
    let daysWithMeals = new Set();

    weekData.forEach(dayData => {
      daysWithMeals.add(dayData.Date);

      MEAL_TYPES.forEach(meal => {
        const mealData = getMealData(dayData, meal);
        const hasAnyFood = FOOD_CATEGORIES.some(category => mealData[category]);

        if (hasAnyFood) {
          totalMealsLogged++;

          // Count each food category
          FOOD_CATEGORIES.forEach(category => {
            if (mealData[category]) {
              categoryStats[category]++;
            }
          });

          // Count sugar levels
          if (mealData['Added Sugar'] && mealData['Added Sugar'] !== '') {
            sugarStats[mealData['Added Sugar']]++;
          }
        }
      });
    });

    // Find most common food category
    const mostCommonFood = Object.entries(categoryStats)
      .sort(([,a], [,b]) => b - a)[0];

    // Calculate sugar frequency
    const totalSugarMeals = Object.values(sugarStats).reduce((sum, count) => sum + count, 0);
    const sugarPercentage = totalMealsLogged > 0 ? 
      ((totalSugarMeals / totalMealsLogged) * 100).toFixed(0) : 0;

    return {
      totalMealsLogged,
      daysWithMeals: daysWithMeals.size,
      mostCommonFood: mostCommonFood[0],
      mostCommonFoodCount: mostCommonFood[1],
      categoryStats,
      sugarPercentage,
      totalSugarMeals
    };
  }, [patientData, startOfWeek, endOfWeek]);



  return (
    <ChartContainer
      title="Meal Contents"
      icon="ðŸ½ï¸"
      isLoading={loading}
      error={error}
      noData={!loading && !error && (!patientData || patientData.length === 0)}
      isExpanded={isExpanded}
      onExpand={onExpand}
      onPrev={handlePrevWeek}
      onNext={handleNextWeek}
      navigationLabel={formatDateRange(startOfWeek, endOfWeek)}
    >
      <div className="meal-contents-chart-container">
        <div className="meal-contents-legends-wrapper">
            <Legend 
              title="Food Categories" 
              items={FOOD_CATEGORIES.map(category => ({
                label: `${CATEGORY_EMOJIS[category]} ${category}`,
                color: null
              }))}
            />
            <Legend 
              title="Sugar Levels" 
              items={SUGAR_CATEGORIES.filter(sugar => sugar !== '').map(sugar => ({
                label: `${SUGAR_EMOJIS[sugar]} ${sugar}`,
                color: null
              }))}
            />
        </div>
        <div className={`meal-chart-wrapper ${isExpanded ? 'expanded' : ''}`} ref={containerRef}>
          <svg 
            width={config.width} 
            height={config.height} 
            viewBox={`0 0 ${config.width} ${config.height}`} 
            className="meal-chart"
            style={{ maxWidth: '100%', height: 'auto' }}
          >
            <XAxis config={config} weekDays={weekDays} />
            <YAxis config={config} />
            <MealGrid 
              config={config} 
              weekDays={weekDays} 
              patientData={patientData} 
            />
          </svg>
        </div>

        {/* Show summary for physician view only */}
        {isPhysicianView && weekSummary && (
          <div className="meal-summary">
            <h4>Week Summary</h4>
            <div className="summary-stats">
              <div className="stat-item">
                <span className="stat-label">Meals Logged:</span>
                <span className="stat-value">
                  {weekSummary.totalMealsLogged}/21 possible
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Days with Meals:</span>
                <span className="stat-value">
                  {weekSummary.daysWithMeals}/7 days
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Most Common Food:</span>
                <span className="stat-value">
                  {CATEGORY_EMOJIS[weekSummary.mostCommonFood]} {weekSummary.mostCommonFood} ({weekSummary.mostCommonFoodCount}x)
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Added Sugar:</span>
                <span className="stat-value">
                  {weekSummary.sugarPercentage}% of meals ({weekSummary.totalSugarMeals}/{weekSummary.totalMealsLogged})
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Food Balance:</span>
                <span className="stat-value">
                  {Object.entries(weekSummary.categoryStats)
                    .sort(([,a], [,b]) => b - a)
                    .slice(0, 3)
                    .map(([cat, count]) => `${CATEGORY_EMOJIS[cat]}${count}`)
                    .join(' ')}
                </span>
              </div>
            </div>
          </div>
        )}
      </div>
    </ChartContainer>
  );
};

export default MealContentsChart;
