/**
 * PatientInfoGrid.js - Patient Information Grid Component
 * 
 * Displays patient information in three aligned boxes that match the visualization grid layout.
 * Replaces the single PatientInfoCard with a more organized, space-efficient layout.
 */

import React from 'react';
import PatientBasicInfo from './PatientBasicInfo';
import PatientConditions from './PatientConditions';
import PatientMedications from './PatientMedications';
import './PatientInfoGrid.css';

const PatientInfoGrid = ({ 
  patientInfo, 
  loading = false, 
  error = null, 
  className = '' 
}) => {
  return (
    <div className={`patient-info-grid ${className}`}>
      <PatientBasicInfo 
        patientInfo={patientInfo}
        loading={loading}
        error={error}
      />
      <PatientConditions 
        patientInfo={patientInfo}
        loading={loading}
        error={error}
      />
      <PatientMedications 
        patientInfo={patientInfo}
        loading={loading}
        error={error}
      />
    </div>
  );
};

export default PatientInfoGrid;
