import React, { useState } from 'react';
import VisualizationWrapper from './VisualizationWrapper';
import './PhysicianVisualizationWrapper.css';

/**
 * Extended VisualizationWrapper for physician view with data summary text boxes
 * This component will be used when you add text boxes to summarize data in the physician view
 */
const PhysicianVisualizationWrapper = ({ 
  id, 
  className, 
  selectedViz, 
  availableVisualizations, 
  allVisualizations,
  onVisualizationChange, 
  onExpand, 
  isExpanded, 
  renderVisualization,
  disabled = false,
  placeholderText = "No data available for this patient.",
  // New props for summary functionality
  showSummary = false,
  summaryText = "",
  onSummaryChange = null,
  summaryPlaceholder = "Enter data summary..."
}) => {
  // Get appropriate placeholder text based on visualization type
  const getSummaryPlaceholder = () => {
    switch (selectedViz) {
      case 'pain':
        return "Enter additional pain assessment notes, treatment recommendations, or follow-up observations...";
      case 'glucose':
        return "Enter weekly/monthly glucose trends, medication adjustments, or dietary recommendations...";
      case 'bloodPressure':
        return "Enter weekly/monthly BP trends, medication effectiveness, or lifestyle recommendations...";
      case 'exercise':
        return "Enter exercise compliance assessment, activity level trends, or fitness recommendations...";
      case 'sleep':
        return "Enter sleep quality assessment, sleep hygiene recommendations, or sleep disorder observations...";
      case 'mood':
        return "Enter mood pattern analysis, mental health observations, or therapeutic recommendations...";
      case 'mealContents':
        return "Enter dietary pattern analysis, nutritional recommendations, or meal planning suggestions...";
      default:
        return "Enter data summary, observations, or recommendations...";
    }
  };
  const [localSummaryText, setLocalSummaryText] = useState(summaryText);

  const handleSummaryChange = (e) => {
    const newText = e.target.value;
    setLocalSummaryText(newText);
    if (onSummaryChange) {
      onSummaryChange(id, newText);
    }
  };

  return (
    <div className={`physician-visualization-wrapper ${className}`}>
      <VisualizationWrapper
        id={id}
        className={className}
        selectedViz={selectedViz}
        availableVisualizations={availableVisualizations}
        allVisualizations={allVisualizations}
        onVisualizationChange={onVisualizationChange}
        onExpand={onExpand}
        isExpanded={isExpanded}
        renderVisualization={renderVisualization}
        disabled={disabled}
        placeholderText={placeholderText}
      />
      
      {/* Summary text box - will be shown when showSummary is true */}
      {showSummary && (
        <div className="summary-section">
          <label htmlFor={`summary-${id}`} className="summary-label">
            Data Summary:
          </label>
          <textarea
            id={`summary-${id}`}
            className="summary-textarea"
            value={localSummaryText}
            onChange={handleSummaryChange}
            placeholder={summaryPlaceholder}
            rows={3}
          />
        </div>
      )}
    </div>
  );
};

export default PhysicianVisualizationWrapper;
