import React, { useState } from 'react';
import GlucoseChart from './components/patient_charts/GlucoseChart';
import BloodPressureChart from './components/patient_charts/BloodPressureChart';
import ExerciseChart from './components/patient_charts/ExerciseChart';
import MoodCalendar from './components/patient_charts/MoodCalendar';
import PainChart from './components/patient_charts/PainChart';
import MealContentsChart from './components/patient_charts/MealContentsChart';
import SleepChart from './components/patient_charts/SleepChart';
import usePatientData from './hooks/usePatientData';
import { VISUALIZATION_CONFIG } from './constants';
import './PatientDashboard.css';

const PatientDashboard = ({ patientId }) => {
  const { data, loading, error } = usePatientData(patientId);

  const allVisualizations = {
    glucose: { ...VISUALIZATION_CONFIG.glucose, component: GlucoseChart },
    bloodPressure: { ...VISUALIZATION_CONFIG.bloodPressure, component: BloodPressureChart },
    exercise: { ...VISUALIZATION_CONFIG.exercise, component: ExerciseChart },
    mood: { ...VISUALIZATION_CONFIG.mood, component: MoodCalendar },
    pain: { ...VISUALIZATION_CONFIG.pain, component: PainChart },
    mealContents: { ...VISUALIZATION_CONFIG.mealContents, component: MealContentsChart },
    sleep: { ...VISUALIZATION_CONFIG.sleep, component: SleepChart },
  };

    const availableVisualizations = React.useMemo(() => {
    if (!data) return {};

    return Object.entries(allVisualizations).reduce((acc, [key, viz]) => {
      const dataKey = `${key}Data`;
      // Special case for meal contents
      if (key === 'mealContents' && data['mealData'] && data['mealData'].length > 0) {

        acc[key] = viz;
      } else if (data[dataKey] && data[dataKey].length > 0) {
        acc[key] = viz;
      }
      return acc;
    }, {});
  }, [data]);

  const [selectedVisualizations, setSelectedVisualizations] = useState({
    box1: 'sleep',
    box2: 'exercise',
    box3: 'mood'
  });

  const [expandedBox, setExpandedBox] = useState(null);

  const handleVisualizationChange = (boxId, visualizationType) => {
    setSelectedVisualizations(prev => ({ ...prev, [boxId]: visualizationType }));
  };

  const handleExpand = (boxId) => {
    setExpandedBox(prev => (prev === boxId ? null : boxId));
  };


  const renderVisualization = (visualizationType, boxId) => {
    const viz = allVisualizations[visualizationType];
    if (!viz) return <div>Invalid visualization</div>;

    const Component = viz.component;
    const isExpanded = expandedBox === boxId;
    return <Component 
      patientId={patientId} 
      isExpanded={isExpanded} 
      onExpand={() => handleExpand(boxId)}
    />;
  };

  const VisualizationBox = ({ boxId, className, isExpanded = false }) => {
    const selectedViz = selectedVisualizations[boxId];
    const viz = allVisualizations[selectedViz];

    return (
      <div className={`visualization-box ${className}`}>
        <div className="visualization-header">
          <select 
            value={selectedViz}
            onChange={(e) => handleVisualizationChange(boxId, e.target.value)}
            style={{ color: viz?.color || '#2c3e50' }}
          >
            {Object.entries(availableVisualizations).map(([key, viz]) => (
              <option key={key} value={key}>{viz.name}</option>
            ))}
          </select>
          <button onClick={() => handleExpand(boxId)} className="expand-button">
            {expandedBox === boxId ? 'Collapse' : 'Expand'}
          </button>
        </div>
        <div className="visualization-content">
          {renderVisualization(selectedViz, boxId)}
        </div>
      </div>
    );
  };

    const patientInfo = data?.patientInfo;

  if (loading) return <div className="loading-screen">Loading patient data...</div>;
  if (error) return <div className="error-screen">Error: {error}</div>;

  const isExpanded = !!expandedBox;

  return (
    <div className="patient-dashboard">
      <div className="patient-info-card">
        <h3>Patient Information</h3>
        {patientInfo ? (
          <>
            <div>
              <div><span>Patient #:</span> {patientInfo.patientNumber}</div>
              <div><span>Age:</span> {patientInfo.age} years</div>
              <div><span>Gender:</span> {patientInfo.gender || 'Not specified'}</div>
              <div><span>Data Available:</span> {patientInfo.dataAvailable}</div>
            </div>

            <div className="conditions-section">
              <span>Conditions:</span>
              {patientInfo.conditions?.length ? (
                <div className="conditions-list">
                  {patientInfo.conditions.map((condition, i) => (
                    <div key={i} className="condition-item">{condition}</div>
                  ))}
                </div>
              ) : (
                <span> No conditions recorded.</span>
              )}
            </div>

            <div className="medications-section">
              <span>Current Medications:</span>
              {patientInfo.detailedMedications?.length ? (
                <div className="medications-list">
                  {patientInfo.detailedMedications.map((med, i) => (
                    <div key={i} className="medication-item">
                      <div className="medication-name">{med.name}</div>
                      <div className="medication-details">
                        <span className="medication-schedule">{med.schedule}</span>
                        {med.category && <span className="medication-category">• {med.category}</span>}
                      </div>
                      {med.dosage && <div className="medication-dosage">{med.dosage}</div>}
                    </div>
                  ))}
                </div>
              ) : (
                <span> No medications recorded.</span>
              )}
            </div>
          </>
        ) : (
          <p>No patient data found.</p>
        )}
      </div>

      {isExpanded ? (
        <VisualizationBox 
          boxId={expandedBox}
          className="expanded-view"
          isExpanded={true}
        />
      ) : (
        <>
          <div className="dashboard-grid">
            <VisualizationBox boxId="box1" className="visualization-box-1" />
            <VisualizationBox boxId="box2" className="visualization-box-2" />
            <VisualizationBox boxId="box3" className="visualization-box-3" />
          </div>
        </>
      )}
    </div>
  );
};

export default PatientDashboard;
