/**
 * ChartWrapper - Common wrapper component for all chart visualizations
 * 
 * This component provides a consistent wrapper structure for all charts,
 * eliminating duplicate container patterns and ensuring consistent styling.
 */

import React from 'react';
import './ChartWrapper.css';

const ChartWrapper = ({ 
  children, 
  title, 
  subtitle, 
  isExpanded = false, 
  onExpand, 
  viewMode = 'patient',
  className = '',
  showExpandButton = true,
  showNavigation = false,
  navigationComponent = null,
  summaryComponent = null,
  legendComponent = null
}) => {
  const handleExpand = () => {
    if (onExpand) {
      onExpand();
    }
  };

  return (
    <div className={`chart-wrapper ${isExpanded ? 'expanded' : ''} ${className}`}>
      {/* Chart Header */}
      <div className="chart-header">
        {title && <h3 className="chart-title">{title}</h3>}
        {subtitle && <h4 className="chart-subtitle">{subtitle}</h4>}
        {showExpandButton && onExpand && (
          <button 
            className={`expand-button ${isExpanded ? 'expanded' : ''}`}
            onClick={handleExpand}
            aria-label={isExpanded ? 'Collapse chart' : 'Expand chart'}
          >
            {isExpanded ? '−' : '+'}
          </button>
        )}
      </div>

      {/* Chart Content */}
      <div className={`chart-content ${isExpanded ? 'expanded' : ''}`}>
        {children}
      </div>

      {/* Chart Footer */}
      {(showNavigation || summaryComponent || legendComponent) && (
        <div className="chart-footer">
          {showNavigation && navigationComponent && (
            <div className="chart-navigation">
              {navigationComponent}
            </div>
          )}
          
          {summaryComponent && (
            <div className="summary-container">
              {summaryComponent}
            </div>
          )}
          
          {legendComponent && (
            <div className="chart-legend">
              {legendComponent}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ChartWrapper;
