import React, { useState, useMemo, useEffect } from 'react';
import PropTypes from 'prop-types';
import usePatientData from '../hooks/usePatientDataImproved';
import Card from './ui/Card';
import LoadingSpinner from './ui/LoadingSpinner';
import ErrorMessage from './ui/ErrorMessage';

// Import unified chart components
import GlucoseChart from './charts/GlucoseChart';
import BloodPressureChart from './charts/BloodPressureChart';
import ExerciseChart from './charts/ExerciseChart';
import MoodCalendar from './charts/MoodCalendar';
import PainChart from './charts/PainChart';
import SleepChart from './charts/SleepChart';
import MealChart from './charts/MealChart';

import './Dashboard.css';

const CHART_TYPES = {
  glucose: { name: 'Blood Glucose', component: GlucoseChart, icon: '🩸' },
  bloodPressure: { name: 'Blood Pressure', component: BloodPressureChart, icon: '❤️' },
  exercise: { name: 'Exercise', component: ExerciseChart, icon: '🏃' },
  mood: { name: 'Mood', component: MoodCalendar, icon: '😊' },
  pain: { name: 'Pain', component: PainChart, icon: '😣' },
  sleep: { name: 'Sleep', component: SleepChart, icon: '😴' },
  meals: { name: 'Meal Contents', component: MealChart, icon: '🍽️' }
};

/**
 * Unified Dashboard component that works for both Patient and Physician views
 * 
 * @param {string} patientId - Patient identifier
 * @param {string} viewMode - 'patient' or 'physician'
 */
function Dashboard({ patientId, viewMode = 'patient' }) {
  const { data, loading, error, retry } = usePatientData(patientId);
  
  // Determine available visualizations based on data
  const availableVisualizations = useMemo(() => {
    if (!data) {
      console.log('🚫 No data available for visualizations');
      return {};
    }

    console.log('📊 Checking data for visualizations:', data);

    const available = Object.entries(CHART_TYPES).reduce((acc, [key, config]) => {
      const dataKey = `${key}Data`;
      let hasData = false;

      // Special case for meal contents
      if (key === 'meals') {
        hasData = data.mealData && Array.isArray(data.mealData) && data.mealData.length > 0;
        console.log(`📊 ${key}: mealData length = ${data.mealData?.length || 0}`);
      } else {
        hasData = data[dataKey] && Array.isArray(data[dataKey]) && data[dataKey].length > 0;
        console.log(`📊 ${key}: ${dataKey} length = ${data[dataKey]?.length || 0}`);
      }

      if (hasData) {
        acc[key] = config;
        console.log(`✅ Added ${key} to available visualizations`);
      } else {
        console.log(`❌ Skipped ${key} - no data available`);
      }

      return acc;
    }, {});

    console.log('📊 Final available visualizations:', Object.keys(available));
    return available;
  }, [data]);
  
  // Chart selection logic based on view mode
  const getDefaultCharts = () => {
    const availableKeys = Object.keys(availableVisualizations);
    
    if (viewMode === 'physician') {
      // Physician view: show all available charts
      const defaultOrder = ['glucose', 'bloodPressure', 'exercise', 'sleep', 'mood', 'pain', 'meals'];
      const orderedAvailable = defaultOrder.filter(key => availableKeys.includes(key));
      const remaining = availableKeys.filter(key => !orderedAvailable.includes(key));
      return [...orderedAvailable, ...remaining];
    } else {
      // Patient view: show first 3 available charts
      return availableKeys.slice(0, 3);
    }
  };

  const [selectedCharts, setSelectedCharts] = useState([]);
  const [expandedChart, setExpandedChart] = useState(null);
  
  // Update selected charts when available visualizations change
  useEffect(() => {
    if (Object.keys(availableVisualizations).length > 0) {
      setSelectedCharts(getDefaultCharts());
    }
  }, [availableVisualizations, viewMode]);

  // Debug information
  console.log('🔍 Dashboard render:', { patientId, viewMode, loading, error, data, availableVisualizations });

  // Loading state
  if (loading) {
    return (
      <div className="dashboard">
        <LoadingSpinner message={`Loading ${viewMode} dashboard for ${patientId}...`} />
        <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
          <p>Debug: Loading state = {loading ? 'true' : 'false'}</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="dashboard">
        <ErrorMessage 
          title="Failed to load patient data"
          message={error}
          onRetry={retry}
          type="error"
        />
        <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
          <p>Debug: Error = {error}</p>
        </div>
      </div>
    );
  }

  // No data available
  if (!data) {
    return (
      <div className="dashboard">
        <Card title="No Data Available">
          <p>No health data is available for patient {patientId}.</p>
          <p>Debug: data = {data ? 'exists' : 'null'}</p>
        </Card>
      </div>
    );
  }

  // Data exists but no visualizations
  if (Object.keys(availableVisualizations).length === 0) {
    return (
      <div className="dashboard">
        <Card title="Data Available but No Visualizations">
          <p>Patient data loaded but no charts available.</p>
          <p>Debug: data keys = {Object.keys(data || {}).join(', ')}</p>
          <p>Debug: glucose data length = {data?.glucoseData?.length || 0}</p>
        </Card>
      </div>
    );
  }

  const handleChartToggle = (chartType) => {
    setSelectedCharts(prev => 
      prev.includes(chartType) 
        ? prev.filter(type => type !== chartType)
        : [...prev, chartType]
    );
  };

  const handleExpandChart = (chartType) => {
    setExpandedChart(expandedChart === chartType ? null : chartType);
  };

  return (
    <div className={`dashboard dashboard--${viewMode}`}>
      {/* Patient Info */}
      {data.patientInfo && (
        <Card 
          title={`📋 Patient Information`}
          className="patient-info"
        >
          <div className="patient-details">
            <div className="detail-item">
              <strong>Patient ID:</strong> {data.patientInfo.patientId}
            </div>
            {data.patientInfo.age && (
              <div className="detail-item">
                <strong>Age:</strong> {data.patientInfo.age}
              </div>
            )}
            {data.patientInfo.gender && (
              <div className="detail-item">
                <strong>Gender:</strong> {data.patientInfo.gender}
              </div>
            )}
            {data.patientInfo.conditions && data.patientInfo.conditions.length > 0 && (
              <div className="detail-item">
                <strong>Conditions:</strong> {data.patientInfo.conditions.join(', ')}
              </div>
            )}
          </div>
        </Card>
      )}

      {/* Chart Selection (for patient view) */}
      {viewMode === 'patient' && (
        <Card 
          title="📊 Select Health Metrics to View"
          className="chart-selector"
        >
          <div className="chart-toggles">
            {Object.entries(availableVisualizations).map(([key, config]) => (
              <label key={key} className="chart-toggle">
                <input
                  type="checkbox"
                  checked={selectedCharts.includes(key)}
                  onChange={() => handleChartToggle(key)}
                />
                <span className="toggle-label">
                  {config.icon} {config.name}
                </span>
              </label>
            ))}
          </div>
        </Card>
      )}

      {/* Charts Grid */}
      <div className={`charts-grid charts-grid--${viewMode}`}>
        {selectedCharts.map(chartType => {
          const config = availableVisualizations[chartType];
          if (!config) return null;

          const ChartComponent = config.component;
          const isExpanded = expandedChart === chartType;

          return (
            <Card
              key={chartType}
              title={`${config.icon} ${config.name}`}
              className={`chart-card ${isExpanded ? 'chart-card--expanded' : ''}`}
              headerActions={
                <button
                  onClick={() => handleExpandChart(chartType)}
                  className="expand-button"
                  aria-label={isExpanded ? 'Collapse chart' : 'Expand chart'}
                >
                  {isExpanded ? '🗙' : '🔍'}
                </button>
              }
            >
              <div className="chart-container">
                <ChartComponent 
                  patientId={patientId}
                  data={data}
                  viewMode={viewMode}
                  isExpanded={isExpanded}
                />
              </div>
            </Card>
          );
        })}
      </div>

      {/* Summary for physician view */}
      {viewMode === 'physician' && (
        <Card 
          title="📈 Clinical Summary"
          className="clinical-summary"
        >
          <div className="summary-grid">
            <div className="summary-item">
              <strong>Data Period:</strong> Last 31 days
            </div>
            <div className="summary-item">
              <strong>Available Metrics:</strong> {Object.keys(availableVisualizations).length}
            </div>
            <div className="summary-item">
              <strong>Last Updated:</strong> {new Date().toLocaleDateString()}
            </div>
          </div>
        </Card>
      )}
    </div>
  );
}

Dashboard.propTypes = {
  patientId: PropTypes.string.isRequired,
  viewMode: PropTypes.oneOf(['patient', 'physician'])
};

export default Dashboard;
