import React, { useState, useMemo } from 'react';
import usePatientData from '../../hooks/usePatientData';
import ChartContainer from '../ChartContainer';
import Legend from '../Legend';
import InfoBox from '../InfoBox';
import './GlucoseChart.css';
import '../ChartStyles.css';

// --- Constants ---
const DAYS_OF_WEEK = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
const RANGE_COLORS = {
  'below range': 'var(--chart-color-warning)',
  'in range': 'var(--chart-color-good)',
  'above range': 'var(--chart-color-danger)',
};
const TIME_LABELS = ['12a', '12p'];
const DEFAULT_WEEK = new Date(2025, 4, 1);

const getChartConfig = (isExpanded) => ({
  width: isExpanded ? 480 : 400,
  height: isExpanded ? 380 : 360,
  padding: { top: 70, right: 30, bottom: 60, left: 50 },
  dayWidth: isExpanded ? 54 : 45,
  yAxisLabels: [0, 4, 8, 12],
  yAxisRange: 12,
  fontSize: {
    yAxis: isExpanded ? 12 : 10,
    yAxisTitle: isExpanded ? 13 : 11,
    timeLabel: isExpanded ? 9 : 7,
    dayLabel: isExpanded ? 13 : 11,
    dateLabel: isExpanded ? 11 : 9,
  },
  barWidth: isExpanded ? 8 : 6,
});

// --- Helper Functions ---
const getPointColor = (range) => RANGE_COLORS[range.toLowerCase()] || RANGE_COLORS['in range'];
const isPreMeal = (measurementType) => measurementType === 'Pre meal';
const getTimePosition = (date, dayIndex, config) => {
  const timeInHours = date.getHours() + date.getMinutes() / 60;
  const timeRatio = timeInHours / 24;
  const dayStart = config.padding.left + dayIndex * config.dayWidth;
  return dayStart + (timeRatio * (config.dayWidth - 10)) + 5;
};

// --- Chart Sub-components ---

const YAxis = ({ config }) => {
  const chartHeight = config.height - config.padding.top - config.padding.bottom;
  const chartWidth = config.width - config.padding.left - config.padding.right;

  return (
    <g className="y-axis">
      <text
        className="y-axis-title"
        x={-(config.padding.top + chartHeight / 2)}
        y={config.padding.left / 2 - 10}
        transform="rotate(-90)"
        textAnchor="middle"
      >
        mmol/L
      </text>
      {config.yAxisLabels.map(label => {
        const y = config.padding.top + chartHeight - (label / config.yAxisRange) * chartHeight;
        return (
          <g key={label}>
            <line
              className="chart-grid-line-horizontal"
              x1={config.padding.left}
              y1={y}
              x2={config.padding.left + chartWidth}
              y2={y}
            />
            <text className="chart-tick-label" x={config.padding.left - 10} y={y} dy="0.3em" textAnchor="end">{label}</text>
          </g>
        );
      })}
    </g>
  );
};

const XAxisAndGrid = ({ config, startOfWeek }) => (
  <g className="x-axis-grid">
    {DAYS_OF_WEEK.map((day, i) => {
      const x = config.padding.left + i * config.dayWidth;
      const currentDay = new Date(startOfWeek);
      currentDay.setDate(startOfWeek.getDate() + i);
      return (
        <g key={i}>
                    <line className="chart-grid-line-vertical" x1={x} y1={config.padding.top} x2={x} y2={config.height - config.padding.bottom} />
          {TIME_LABELS.map((timeLabel, timeIndex) => (
            <text key={timeIndex} className="time-label" x={x + (timeIndex === 0 ? 5 : config.dayWidth - 5)} y={config.height - config.padding.bottom + 15} textAnchor={timeIndex === 0 ? 'start' : 'end'}>
              {timeLabel}
            </text>
          ))}
          <text className="x-axis-day-label" x={x + config.dayWidth / 2} y={config.height - config.padding.bottom + 30} textAnchor="middle">{day}</text>
          <text className="x-axis-date-label" x={x + config.dayWidth / 2} y={config.height - config.padding.bottom + 45} textAnchor="middle">{currentDay.getDate()}</text>
        </g>
      );
    })}
  </g>
);

const DataPoints = ({ weekData, config }) => {
  const chartHeight = config.height - config.padding.top - config.padding.bottom;
  return (
    <g className="data-points">
      {weekData.map((reading, index) => {
        const dayIndex = reading.date.getDay();
        const x = getTimePosition(reading.date, dayIndex, config);
        const y = config.padding.top + chartHeight - (reading.value / config.yAxisRange) * chartHeight;
        const color = getPointColor(reading.range);
        const isPre = isPreMeal(reading.measurementType);
        return (
          <rect
            key={index}
            x={x - config.barWidth / 2}
            y={y}
            width={config.barWidth}
            height={(reading.value / config.yAxisRange) * chartHeight}
            fill={isPre ? 'none' : color}
            stroke={color}
            strokeWidth={isPre ? 2 : 1}
          >
            <title>{`${reading.value} mmol/L - ${reading.range} - ${reading.measurementType} - ${reading.date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}`}</title>
          </rect>
        );
      })}
    </g>
  );
};

const Chart = ({ weekData, isExpanded, startOfWeek }) => {
  const config = getChartConfig(isExpanded);
  return (
    <div className="glucose-svg-container">
      <svg width="100%" height="100%" viewBox={`0 0 ${config.width} ${config.height}`} className="glucose-svg">
        <rect width={config.width} height={config.height} fill="white" />
        <XAxisAndGrid config={config} startOfWeek={startOfWeek} />
        <YAxis config={config} />
        <DataPoints weekData={weekData} config={config} />
      </svg>
    </div>
  );
};

// --- Main Component ---
const GlucoseChart = ({ patientId, isExpanded = false, onExpand, isPhysicianView = false }) => {
  const healthRangeLegendItems = Object.entries(RANGE_COLORS).map(([label, color]) => ({
    label: label.charAt(0).toUpperCase() + label.slice(1),
    color,
  }));

  const mealTimeLegendItems = [
    { 
      label: 'Pre-meal', 
      shape: 'outlined',
      shapeStyle: { borderColor: 'var(--chart-color-text-secondary)' },
      description: 'Blood glucose measurements taken before meals'
    },
    { 
      label: 'Post-meal', 
      shape: 'filled',
      shapeStyle: { backgroundColor: 'var(--chart-color-text-secondary)' },
      description: 'Blood glucose measurements taken after meals'
    },
  ];
  const { glucoseData, loading, error } = usePatientData(patientId, 'glucose');
  const [currentWeek, setCurrentWeek] = useState(DEFAULT_WEEK);

  const handlePrevWeek = () => setCurrentWeek(prev => new Date(new Date(prev).setDate(prev.getDate() - 7)));
  const handleNextWeek = () => setCurrentWeek(prev => new Date(new Date(prev).setDate(prev.getDate() + 7)));

  const startOfWeek = new Date(currentWeek);
  startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay());
  startOfWeek.setHours(0, 0, 0, 0);

  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(endOfWeek.getDate() + 6);
  endOfWeek.setHours(23, 59, 59, 999);

  const weekData = glucoseData.filter(d => d.date >= startOfWeek && d.date <= endOfWeek);

  // Calculate summary statistics for physician view
  const weekSummary = useMemo(() => {
    if (!weekData.length) return null;

    const readings = weekData.filter(d => d.value && d.value > 0);
    const preMealReadings = readings.filter(d => d.measurementType === 'Pre meal');
    const postMealReadings = readings.filter(d => d.measurementType === 'Post meal');

    const avgGlucose = readings.length > 0 ? 
      (readings.reduce((sum, val) => sum + val.value, 0) / readings.length).toFixed(1) : 0;
    
    const avgPreMeal = preMealReadings.length > 0 ? 
      (preMealReadings.reduce((sum, val) => sum + val.value, 0) / preMealReadings.length).toFixed(1) : 0;
    
    const avgPostMeal = postMealReadings.length > 0 ? 
      (postMealReadings.reduce((sum, val) => sum + val.value, 0) / postMealReadings.length).toFixed(1) : 0;

    const highReadings = readings.filter(d => d.range.toLowerCase() === 'above range').length;
    const lowReadings = readings.filter(d => d.range.toLowerCase() === 'below range').length;
    const inRangeReadings = readings.filter(d => d.range.toLowerCase() === 'in range').length;

    const rangePercentage = readings.length > 0 ? 
      ((inRangeReadings / readings.length) * 100).toFixed(0) : 0;

    const daysWithReadings = new Set(weekData.map(d => d.date.toDateString())).size;

    return {
      avgGlucose,
      avgPreMeal,
      avgPostMeal,
      highReadings,
      lowReadings,
      inRangeReadings,
      rangePercentage,
      daysWithReadings,
      totalReadings: readings.length
    };
  }, [weekData]);

  const formatDateRange = (start, end) => {
    const startStr = start.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const endStr = end.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    return `${startStr} to ${endStr}`;
  };

  return (
    <ChartContainer
      title="Blood Glucose"
      icon="ðŸ©¸"
      isLoading={loading}
      error={error}
      noData={!loading && !error && (!glucoseData || glucoseData.length === 0)}
      isExpanded={isExpanded}
      onExpand={onExpand}
      onPrev={handlePrevWeek}
      onNext={handleNextWeek}
      navigationLabel={formatDateRange(startOfWeek, endOfWeek)}
    >
      <div className="glucose-chart-container">
        <div className="glucose-legends-wrapper">
          <Legend title="Health Range" items={healthRangeLegendItems} />
          <Legend title="Prandial State" items={mealTimeLegendItems} />
        </div>
        <div className={`glucose-chart-wrapper ${isExpanded ? 'expanded' : ''}`}>
          <Chart weekData={weekData} isExpanded={isExpanded} startOfWeek={startOfWeek} />
        </div>
        
        {/* Show InfoBox for patient view, summary for physician view */}
        {isPhysicianView && weekSummary ? (
          <div className="chart-summary">
            <h4>Week Summary</h4>
            <div className="summary-stats">
              <div className="stat-item">
                <span className="stat-label">Average Glucose:</span>
                <span className="stat-value">
                  {weekSummary.avgGlucose} mmol/L
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Pre-meal Average:</span>
                <span className="stat-value">
                  {weekSummary.avgPreMeal} mmol/L
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Post-meal Average:</span>
                <span className="stat-value">
                  {weekSummary.avgPostMeal} mmol/L
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">In Target Range:</span>
                <span className="stat-value in-range">
                  {weekSummary.rangePercentage}% ({weekSummary.inRangeReadings}/{weekSummary.totalReadings})
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">High/Low Readings:</span>
                <span className="stat-value">
                  {weekSummary.highReadings} high, {weekSummary.lowReadings} low
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Reading Days:</span>
                <span className="stat-value">
                  {weekSummary.daysWithReadings}/7 days
                </span>
              </div>
            </div>
          </div>
        ) : !isPhysicianView ? (
          <InfoBox 
            title="Blood Glucose Information"
            content="These are general targets for adults with diabetes: For blood sugar taken before a meal, the target is 4.0 to 7.0 mmol/L. For blood sugar two hours after a meal, the target is 5.0 to 10.0 mmol/L. The general target for an A1C is 7.0% or less. Always consult your healthcare provider to confirm the right targets for you."
          />
        ) : null}
      </div>
    </ChartContainer>
  );
};

export default GlucoseChart;
