/**
 * PatientConditions.js - Patient Conditions Component
 * 
 * Displays patient medical conditions and diagnoses.
 * Part of the split patient information layout.
 */

import React from 'react';
import './PatientInfoBox.css';

const PatientConditions = ({ 
  patientInfo, 
  loading = false, 
  error = null, 
  className = '' 
}) => {
  if (loading) {
    return (
      <div className={`patient-info-box conditions ${className}`}>
        <h3>Medical Conditions</h3>
        <p>Loading conditions...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`patient-info-box conditions ${className}`}>
        <h3>Medical Conditions</h3>
        <p>Error loading data.</p>
      </div>
    );
  }

  if (!patientInfo) {
    return (
      <div className={`patient-info-box conditions ${className}`}>
        <h3>Medical Conditions</h3>
        <p>No patient data found.</p>
      </div>
    );
  }

  return (
    <div className={`patient-info-box conditions ${className}`}>
      <h3>Medical Conditions</h3>
      <div className="info-content">
        {patientInfo.conditions?.length ? (
          <div className="conditions-list">
            {patientInfo.conditions.map((condition, i) => (
              <div key={i} className="condition-item">
                <span className="condition-bullet">â€¢</span>
                <span className="condition-text">{condition}</span>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-data">No conditions recorded</div>
        )}
      </div>
    </div>
  );
};

export default PatientConditions;
